export class TableCanvasComponent implements OnInit, OnDestroy {
  @Input({ required: true }) links$!: BehaviorSubject<TableLinkModel[]>;
  @Input({ required: true }) portEvent!: Observable<PortEvent>;

  readonly model: TableCanvasModel;

  protected readonly dragging$ = new BehaviorSubject<boolean>(false);
  private readonly destroy$ = new Subject<void>();

  protected readonly tempLinkModel: TableLinkModel = new TableLinkModel(
    new BehaviorSubject<TablePortModel>(
      new TablePortModel('sourceTemp', new BehaviorSubject<TableTreePortType>(TableTreePortType.SOURCE), '')
    ),
    new BehaviorSubject<TablePortModel>(
      new TablePortModel('targetTemp', new BehaviorSubject<TableTreePortType>(TableTreePortType.TARGET), '')
    )
  );

  protected readonly doLinkModel: TableLinkModel = new TableLinkModel(
    new BehaviorSubject<TablePortModel>(
      new TablePortModel('sourceDO', new BehaviorSubject<TableTreePortType>(TableTreePortType.SOURCE), '')
    ),
    new BehaviorSubject<TablePortModel>(
      new TablePortModel('targetDO', new BehaviorSubject<TableTreePortType>(TableTreePortType.TARGET), '')
    )
  );

  private sourcePort: TablePortModel | undefined = undefined;
  private targetPort: TablePortModel | undefined = undefined;

  get htmlElement(): HTMLElement {
    return this.elementRef.nativeElement as HTMLElement;
  }

  constructor(
    private elementRef: ElementRef,
    private snackBar: MatSnackBar
  ) {
    this.model = { htmlElement: this.htmlElement };
  }

  ngOnInit(): void {
    this.links$
      .pipe(
        takeUntil(this.destroy$),
        map(links => links.length === 0)
      )
      .subscribe(linksLength => {
        if (linksLength) {
        }
      });

    this.portEvent.pipe(takeUntil(this.destroy$)).subscribe(event => {
      switch (event.type) {
        case 'start':
          this.onDragStart(event.event, event.port);
          break;
        case 'over':
          this.onDragOver(event.event);
          break;
        case 'drop':
          this.onDragDrop(event.event, event.port);
          break;
        case 'end':
          this.onDragEnd(event.event, event.port);
          break;
      }
    });
  }

  private onDragStart(_event: DragEvent, port?: TablePortModel): void {
    if (port) {
      if (port.portType.value === TableTreePortType.SOURCE) {
        this.sourcePort = port;
      } else {
        this.targetPort = port;
      }

      this.dragging$.next(true);
    }
  }

  private onDragOver(event: MouseEvent): void {
    const rect = this.htmlElement.getBoundingClientRect();

    if (this.dragging$.value) {
      if (this.sourcePort) {
        this.sourcePort.update$.next();
        this.tempLinkModel.sourcePort$.value.coords$.next(this.sourcePort.coords$.value);
        this.tempLinkModel.targetPort$.value.coords$.next({
          x: event.clientX - rect.left,
          y: event.clientY - rect.top
        });
      } else if (this.targetPort) {
        this.targetPort.update$.next();
        this.tempLinkModel.sourcePort$.value.coords$.next({
          x: event.clientX - rect.left,
          y: event.clientY - rect.top
        });
        this.tempLinkModel.targetPort$.value.coords$.next(this.targetPort.coords$.value);
      }
    }
  }

  private onDragDrop(_event: DragEvent, port?: TablePortModel): void {
    if (port) {
      if (port.portType.value === TableTreePortType.SOURCE && !this.sourcePort) {
        this.sourcePort = port;
      } else if (port.portType.value === TableTreePortType.TARGET && !this.targetPort) {
        this.targetPort = port;
      }
    }
  }

  private onDragEnd(_event: DragEvent, _port?: TablePortModel): void {
    if (this.sourcePort && this.targetPort) {
      if (this.sourcePort.children$.value.length === 0 && this.targetPort.children$.value.length === 0) {
        this.createNewLink(this.sourcePort, this.targetPort);
      } else if (this.sourcePort.doType === this.targetPort.doType) {
        this.createNewLink(this.sourcePort, this.targetPort);
      } else {
        if (
          (this.sourcePort.children$.value.length !== 0 && this.targetPort.children$.value.length === 0) ||
          (this.targetPort.children$.value.length !== 0 && this.sourcePort.children$.value.length === 0)
        ) {
          this.snackBar.open('Атрибуты могут быть связаны только с атрибутами', 'Закрыть', {
            duration: 3000
          });
        } else if (this.sourcePort.doType !== this.targetPort.doType) {
          this.snackBar.open('Объекты должны быть одного типа', 'Закрыть', {
            duration: 3000
          });
        }
      }
    }

    this.dragging$.next(false);
    this.sourcePort = undefined;
    this.targetPort = undefined;
    this.tempLinkModel.sourcePort$.value.coords$.next({ x: 0, y: 0 });
    this.tempLinkModel.targetPort$.value.coords$.next({ x: 0, y: 0 });
  }

  private createNewLink(sourcePort: TablePortModel, targetPort: TablePortModel) {
    const newLink = new TableLinkModel(
      new BehaviorSubject<TablePortModel>(sourcePort),
      new BehaviorSubject<TablePortModel>(targetPort)
    );

    this.links$.value.push(newLink);
    this.links$.next(this.links$.value);
  }

  ngOnDestroy(): void {
    this.destroy$.next();
  }
}

