export default class Table {
  constructor(name) {
    Object.defineProperty(this, "name", {
      value: name,
      enumerable: true,
      writable: true,
      configurable: true,
    });
    this.columnsNames = [];
    this.blockSize = 1000;
    this.filter = new Filters(this);
    this.df = null;
    console.log("Constructed ", name);
  }

  async fetchDataBlock(offset) {
    const columnList = this.columnsNames.join(", ");
    const query = `SELECT ${columnList} FROM ${this.name} LIMIT ? OFFSET ?`;
    const [rows] = await pool.execute(query, [this.blockSize, offset]);
    return rows;
  }

  async setTableColumns(req, res) {
    try {
      this.columnsNames = await this.getTableColumns(req.params.tableName);
      res.json({ columns: this.columnsNames });
    } catch (error) {
      console.error(error);
      res.status(500).json({
        error: "An error occurred while fetching table structure",
      });
    }
  }

  async getDataFromTable(req, res) {
    const { offset = 0 } = req.query;
    try {
      if (this.df === null) {
        this.columnsNames = await this.getTableColumns();
        let offsetCounter = 0;

        // Initialize an empty DataFrame with the correct columns
        this.df = new dfd.DataFrame([], { columns: this.columnsNames });

        // Fetch data in blocks and append to the DataFrame
        while (true) {
          const dataBlock = await this.fetchDataBlock(offsetCounter);
          if (dataBlock.length === 0) {
            break;
          }

          // Create a DataFrame for the current block and append it to the main DataFrame
          const blockDf = new dfd.DataFrame(dataBlock, { columns: this.columnsNames });
          this.df = dfd.concat({ dfList: [this.df, blockDf], axis: 0 });

          offsetCounter += this.blockSize;
        }

        this.df.print();
      } else {
        console.log("DataFrame already exists, skipping data fetch.");
      }

      res.json(this.df);
    } catch (error) {
      console.error(error);
      res.status(500).json({
        error: "An error occurred while fetching data",
      });
    }
  }

  async getTableColumns() {
    const [rows] = await pool.query(`SHOW COLUMNS FROM ${this.name}`);
    return rows.map((row) => row.Field);
  }
