    @EnvironmentObject var appState: AppState
    @State var questionsData: [QuestionData]

    @State private var currentQuestionIndex = 0
    @State private var score = 0
    @State private var showingExplanation = false
    @State private var quizEnded = false

    var body: some View {
        VStack {
            if questionsData.isEmpty {
                Text("No questions available")
                    .padding()
            } else if !quizEnded && !appState.showingWrongAnswers {
                HStack {
                    Text("Question: \(currentQuestionIndex + 1)/\(questionsData.count)")
                        .padding()
                    Spacer()
                    Text("Score: \(score)/\(questionsData.count)")
                        .padding()
                }

                Text("Question: \(questionsData[currentQuestionIndex].question)")
                    .padding()

                ForEach(0..<questionsData[currentQuestionIndex].options.count, id: \.self) { index in
                    Button(action: {
                        self.checkAnswer(option: index)
                    }) {
                        Text(self.questionsData[self.currentQuestionIndex].options[index])
                            .foregroundColor(.white)
                            .padding()
                            .frame(maxWidth: .infinity)
                            .background(Color.blue)
                            .cornerRadius(10)
                    }
                    .padding(.horizontal)
                }
            } else if appState.showingWrongAnswers {
                // Wrong answers page
                if appState.wrongAnswers.isEmpty {
                    Text("No wrong answers to review")
                        .padding()
                } else {
                    List(appState.wrongAnswers) { question in
                        VStack(alignment: .leading) {
                            Text(question.question)
                                .font(.headline)
                            ForEach(0..<question.options.count, id: \.self) { optionIndex in
                                Button(action: {
                                    self.checkAnswer(option: optionIndex, fromWrongAnswers: true, questionIndex: self.appState.wrongAnswers.firstIndex(of: question)!)
                                }) {
                                    Text(question.options[optionIndex])
                                }
                            }
                        }
                    }
                }
            } else {
                // Quiz ended page
                Text("Quiz Ended")
                    .font(.title)
                    .padding()
                Text("Final Score: \(score)/\(questionsData.count)")
                    .font(.headline)
                    .padding()

                // Show wrong answers page button
                Button("Review Wrong Answers") {
                    appState.showingWrongAnswers = true
                }
                .padding()
            }

            Button("Restart Quiz") {
                self.restartQuiz()
            }
            .padding()
        }
        .navigationTitle("Quiz")
        .toolbar {
            if appState.showingWrongAnswers {
                ToolbarItem(placement: .navigationBarTrailing) {
                    Button("Clear Record") {
                        appState.wrongAnswers.removeAll()
                    }
                }
            }
        }
        .alert(isPresented: $showingExplanation) {
            Alert(
