class FeedOverviewV2Fragment : ComposeContentMviFragment<
        FeedOverviewV2FragmentViewState,
        FeedOverviewV2FragmentViewEffect,
        FeedOverviewV2FragmentViewEvent,
        FeedOverviewV2FragmentVM
        >() {

    override val model: FeedOverviewV2FragmentVM by viewModel()
    private val actionHandler: ActionHandler by inject { parametersOf(this@FeedOverviewV2Fragment) }
    private val applicationLockManager: ApplicationLockManager by inject()
    private val newFeaturesPromoManager: NewFeaturesPromoManager by inject()
    override val layoutRes = R.layout.common_compose_fragment_whole

    private val postNotificationHelper = PostNotificationHelperImpl(this)

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        requestPostNotificationsPermission()
    }

    override fun onResume() {
        super.onResume()
        logScreenView(FirebaseScreens.myEvents)
        model.updateNewsUnread()
        if (!applicationLockManager.isPinSet && !applicationLockManager.isApplicationUnlocking) {
            navigateToApplicationLockScreen(ApplicationLockFragment.LockMode.NEW_PIN)
        }
        newFeaturesPromoManager.navigateToNewFeaturePromoIfUpdated(this)
    }

    override fun onDestroyView() {
        dismissTutorialFlowIfExists()
        super.onDestroyView()
    }

    override fun onEffect(effect: FeedOverviewV2FragmentViewEffect) {
        when (effect) {
            is FeedOverviewV2FragmentViewEffect.NavigateToFeedDetail -> AppLink(effect.urlLink).navigateTo(
                this
            )

            is FeedOverviewV2FragmentViewEffect.NavigateToProfileScreen -> navigateToSomeProfileScreen(
                effect.singleProfile
            )

            is FeedOverviewV2FragmentViewEffect.NavigateToInvoiceDetail -> navigateToInvoiceDetailFragment(
                effect.invoiceId
            )

            FeedOverviewV2FragmentViewEffect.ShowReviewDialog -> appContext.reviewManager.showReviewDialog(
                parentFragmentManager,
                viewLifecycleOwner
            ) {
                model.onResume(viewLifecycleOwner)
            }

            is FeedOverviewV2FragmentViewEffect.NavigateToDocumentImport -> {
                navigateToAppointmentDetailScreen(effect.id)
                navigateToImportDocumentationScreen(effect.id)
            }

            is FeedOverviewV2FragmentViewEffect.NavigateToQuestionnaire -> navigateToQuestionnaireIntroFragment(
                effect.id
            )

            is FeedOverviewV2FragmentViewEffect.NavigateToFeedSettingScreen -> navigateToFeedSettingsScreen()
            FeedOverviewV2FragmentViewEffect.NavigateToSearchScreen -> navigateToMyHealthSearchScreen(
                ""
            )

            is FeedOverviewV2FragmentViewEffect.NavigateByAppBarAction -> navigateByAppBarAction(
                effect.action
            )

            FeedOverviewV2FragmentViewEffect.NavigateBack -> goBack()
            FeedOverviewV2FragmentViewEffect.NavigateToCustomerCare -> handleCallCenterAction(
                childFragmentManager
            )

            is FeedOverviewV2FragmentViewEffect.HandleAction -> launch {
                actionHandler.handleAction(effect.action)
            }

            is FeedOverviewV2FragmentViewEffect.NavigateToDigiCheckInOverview -> navigateToDigiCheckInOverviewScreen(
                null,
                null
            )
        }
    }



    @Composable
    override fun Render(state: FeedOverviewV2FragmentViewState) {
        Box {
            val contentData = state.contentData
            FeedOverviewV2FragmentScreen(state, model::onEvent)
            LaunchedEffect(contentData) {
                handleDeepLink()
                showTutorialFlowIfIndented()
            }
        }
    }

    private fun requestPostNotificationsPermission() {
        if (Build.VERSION.SDK_INT >= Build.VERSION_CODES.TIRAMISU) {
            postNotificationHelper.withPostNotification(requireActivity()) {}
        }
    }

    private fun showTutorialFlowIfIndented() {
        if (!appContext.shouldShowTutorialFlow()) return
        findFeedTutorialFragment()?.let { return }
        FeedTutorialPagerFragment().show(childFragmentManager, TAG_TUTORIAL)
    }

    private fun dismissTutorialFlowIfExists() {
        findFeedTutorialFragment()?.dismissAllowingStateLoss()

    }

    private fun findFeedTutorialFragment() =
        childFragmentManager.findFragmentByTag(TAG_TUTORIAL) as? DialogFragment

    companion object {
        const val TAG_TUTORIAL = "TAG_TUTORIAL"
    }

    @Composable
    fun FeedOverviewV2FragmentScreen(
        state: FeedOverviewV2FragmentViewState,
        onEvent: FeedOverviewV2FragmentOnEvent
    ) {
        val coroutineScope = rememberCoroutineScope()
        val context = LocalContext.current

        val click by remember {
            mutableStateOf({ item: IDataItem ->
                onEvent(
                    FeedOverviewV2FragmentViewEvent.ClickFeedItem(
                        item
                    )
                )
            })
        }

        val contentData = state.contentData
        val itemSpacing = spaces.cardSpacing

        ScreenScaffoldBasicNew(
            topBar = {
                FeedTopAppBar(
                    title = stringResource(R.string.eucapp_feed_overview_title),
                    profiles = {
                        state.contentData?.let {
                            ItemProfiles(it.profiles, it.feedProfiles, it.allFeedProfilesActive) {
                                onEvent(FeedOverviewV2FragmentViewEvent.ClickToolbarOverlap)
                            }
                        }
                    },
                    filterActive = contentData?.feedSetting?.isFilterActive() == true,
                    onActionClick = { onEvent(FeedOverviewV2FragmentViewEvent.ClickAppAction(it)) })
            },
            bottomBar = {
                DefaultNavigationBar(
                    selectedItem = NavigationBarItemAction.Feed,
                    onActionClick = { onEvent(FeedOverviewV2FragmentViewEvent.ClickAppAction(it)) },
                    showNewsBadge = state.additionalData.isNewsUnread
                )
            },
            onRefresh = { onEvent(FeedOverviewV2FragmentViewEvent.Refresh) },
            contentLoading = state.isLoading,
            content = {
                if (contentData == null) return@ScreenScaffoldBasicNew
                val scrollState = rememberLazyListState()
                val feedData = contentData.feedData

                Column(modifier = Modifier.fillMaxWidth()) {
                    val preparationItem = state.contentData.feedData.appointmentPreparationItem
                    if (preparationItem != null && preparationItem.item?.owner != null) {
                        PrepareAppointmentBanner(
                            bannerColors = BannerDefaults.primaryBannerColors,
                            iconMetadata = Icons.Main.CircleInfoBig,
                            highlightedText = preparationItem.toHighlightedText(context),
                            profile = state.contentData.mainProfile.toCurrentViewModel(preparationItem.item.owner),
                            buttons = {
                                PrimaryFilledButton(onClick = {
                                    navigateToPrepareForAppointmentScreen(
                                        preparationItem.reservationId ?: ""
                                    )
                                }, text = stringResource(id = R.string.eucapp_feed_prepare_apointment_read_this))
                            }
                        )
                    }

                    if (feedData.isFilterEmpty) {
                        ItemFilterEmpty()
                    } else {
                        val preLastDelta by remember(contentData.feedData) {
                            derivedStateOf {
                                val layoutInfo = scrollState.layoutInfo
                                val visibleItemsInfo = layoutInfo.visibleItemsInfo

                                if (layoutInfo.totalItemsCount == 0) return@derivedStateOf 0

                                val firstVisibleItem = visibleItemsInfo.first()
                                val lastVisibleItem = visibleItemsInfo.last()

                                val viewportHeight =
                                    layoutInfo.viewportEndOffset + layoutInfo.viewportStartOffset

                                val isScrollable = !(firstVisibleItem.index == 0 &&
                                        firstVisibleItem.offset == 0 &&
                                        lastVisibleItem.index + 1 == layoutInfo.totalItemsCount &&
                                        lastVisibleItem.offset + lastVisibleItem.size <= viewportHeight)

                                if (isScrollable) 0 else viewportHeight - (lastVisibleItem.offset + lastVisibleItem.size)
                            }
                        }

                        LazyColumn(
                            modifier = Modifier
                                .fillMaxWidth()
                                .weight(1f), state = scrollState
                        ) {
                            feedData.sections.forEach { section ->
                                val sectionHeaderItem = section.section
                                if (sectionHeaderItem != null) itemStickyHeader(sectionHeaderItem)
                                itemsIndexedPadded(section.items, itemSpacing) { index, item ->
                                    Box(
                                        modifier = Modifier
                                            .fillMaxWidth()
                                            .padding(horizontal = spaces.default)
                                    ) {
                                        RenderItem(item, click, index)
                                    }
                                }
                            }
                            item {
                                ItemFeedMoreInArchive(
                                    modifier = Modifier.graphicsLayer {
                                        translationY = preLastDelta.toFloat()
                                    }
                                )
                            }
                        }

                        if (FeatureFlags.isEnabled(Feature.DIGI_CHECK_IN)) {
                            val appContext = get<AppContext>()
                            val showDigiCheckInBar = appContext.showDigiCheckInBarFlow.collectAsState()
                            if (showDigiCheckInBar.value) {
                                DigiCheckInBar(onCheckInClicked = {
                                    onEvent(
                                        FeedOverviewV2FragmentViewEvent.DigiCheckInButtonClicked
                                    )
                                })
                            }
                        }
                        VerticalSpacer(80.dp)
                    }
                }

                val context = LocalContext.current
                LaunchedEffect(contentData.indexForScrolling) {
                    if (contentData.indexForScrolling.valid) {
                        coroutineScope.launch {
                            delay(500)
                            val scrollItem =
                                contentData.feedData.flatten.elementAtOrNull(contentData.indexForScrolling.value)

                            val scrollOffset =
                                if (scrollItem != null && scrollItem !is FeedSectionHeaderItem) (-50).dpToPx(
                                    context.resources
                                ) else 0

                            scrollState.animateScrollToItem(
                                contentData.indexForScrolling.value,
                                scrollOffset
                            )
                            onEvent(FeedOverviewV2FragmentViewEvent.ConsumeIndexForScrolling)
                        }
                    }
                }

                if(state.updateState == UpdateState.UPDATE_AVAILABLE) {
                   navigateToUpdateDialogScreen()
                }
            }
        )
    }
}

