@Composable
fun RenderItem(
    item: IDataItem,
    click: (IDataItem) -> Unit,
    index: Int
) {
    when (item) {
        is FeedInvoiceViewModel -> ItemFeedInvoice(item, onClick = click)
        is FeedPrescriptionViewModel -> ItemFeedPrescription(item, onClick = click)
        is FeedMedicalRecordViewModel -> ItemFeedMedicalRecord(item, onClick = click)
        is FeedDocumentationImportItem -> ItemFeedDocumentationImport(item, onClick = click)
        is FeedConfirmAppointmentViewModel -> ItemFeedConfirmAppointment(item, onClick = click)
        is FeedPreventionViewModel -> ItemFeedPrevention(item, onClick = click)
        is FeedMessageViewModel -> ItemFeedMessage(item, onClick = click)
        is FeedMedicalViewModel -> ItemFeedMedical(viewModel = item, onClick = click)
        is FeedReviewItem -> ReviewCardButton() { click(item) }
        is FeedQuestionnaireItem -> ItemFeedQuestionnaire(item, onClick = click)
        is FeedMoreInArchiveItem -> ItemFeedMoreInArchive()
        is FeedCancelledAppointmentViewModel -> ItemFeedCancelledAppointment(item, onClick = click)
        is FeedPastMedicalViewModel -> ItemFeedPastMedical(dataItem = item, onClick = click)
        is FeedMedicationItem -> ItemFeedMedication(click = click, item)
        is FeedEmptyFutureFeedItem -> ItemFeedEmptyFuture(click = click, item = item, index = index)
        else -> ItemFeedOverview(item)
    }
}

