% 贝塞尔曲线函数
function B = bezierCurve(P, t)
    n = size(P, 1) - 1;
    B = zeros(1, size(P, 2));
    for i = 0:n
        B = B + nchoosek(n, i) * (1-t)^(n-i) * t^i * P(i+1, :);
    end
end

% 改进避障算法函数
function B = avoidObstacle(B, obstacles)
    for i = 1:length(obstacles)
        distance = norm(B - obstacles(i).center);
        if distance < obstacles(i).envelope_radius
            repulsion_vector = B - obstacles(i).center;
            repulsion_vector = repulsion_vector / norm(repulsion_vector);
            B = B + 4.5 * repulsion_vector;
        end
    end
end

