void flipTextureVertically(GLuint tex, int width, int height)
{
GLuint tmpTex;
glGenTextures(1, &tmpTex);


glBindTexture(GL_TEXTURE_2D, tmpTex);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_NEAREST);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_NEAREST);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_EDGE);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_EDGE);
glTexImage2D(GL_TEXTURE_2D, 0, GL_RGBA, width, height, 0, GL_RGBA, GL_UNSIGNED_BYTE, nullptr);
glBindTexture(GL_TEXTURE_2D, 0);


GLuint buf[2];
glGenFramebuffers(2, buf);

// copy
glBindFramebuffer(GL_READ_FRAMEBUFFER, buf[0]);
glFramebufferTexture2D(GL_READ_FRAMEBUFFER, GL_COLOR_ATTACHMENT0, GL_TEXTURE_2D, tex, 0);

glBindFramebuffer(GL_DRAW_FRAMEBUFFER, buf[1]);
glFramebufferTexture2D(GL_DRAW_FRAMEBUFFER, GL_COLOR_ATTACHMENT0, GL_TEXTURE_2D, tmpTex, 0);

glBlitFramebuffer(0, 0, width, height, 0, 0, width, height, GL_COLOR_BUFFER_BIT, GL_NEAREST);

// flip copy
glBindFramebuffer(GL_READ_FRAMEBUFFER, buf[1]);
glFramebufferTexture2D(GL_READ_FRAMEBUFFER, GL_COLOR_ATTACHMENT0, GL_TEXTURE_2D, tmpTex, 0);

glBindFramebuffer(GL_DRAW_FRAMEBUFFER, buf[0]);
glFramebufferTexture2D(GL_DRAW_FRAMEBUFFER, GL_COLOR_ATTACHMENT0, GL_TEXTURE_2D, tex, 0);

glBlitFramebuffer(0, 0, width, height, 0, height, width, 0, GL_COLOR_BUFFER_BIT, GL_NEAREST);

glDeleteTextures(1, &tmpTex);
glDeleteFramebuffers(2, buf);
}

#define SBS_WIDTH (3840*2)
#define SBS_HEIGHT (2160)
GLuint loadSideBySideTextureFromFile(const std::wstring& file1_name, const std::wstring& file2_name)
{
auto decoded_texture1 = xrd_xr::graphics::LoadWICTextureFromFile(file1_name);
auto decoded_texture2 = xrd_xr::graphics::LoadWICTextureFromFile(file2_name);
auto width = static_cast(decoded_texture1->width);
auto height = static_cast(decoded_texture1->height);


GLuint tex;
glGenTextures(1, &tex);
glBindTexture(GL_TEXTURE_2D, tex);

glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MAG_FILTER, GL_LINEAR);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_MIN_FILTER, GL_LINEAR);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_S, GL_CLAMP_TO_EDGE);
glTexParameteri(GL_TEXTURE_2D, GL_TEXTURE_WRAP_T, GL_CLAMP_TO_EDGE);
glPixelStorei(GL_UNPACK_ROW_LENGTH, static_cast<GLint>(decoded_texture1->row_pitch) / 4);
glTexImage2D(GL_TEXTURE_2D, 0, GL_RGBA, SBS_WIDTH, SBS_HEIGHT, 0, GL_RGBA, GL_UNSIGNED_BYTE, nullptr);
glTexSubImage2D(GL_TEXTURE_2D, 0, 0, 0, width, height, GL_RGBA, GL_UNSIGNED_BYTE, decoded_texture1->data.get());
glTexSubImage2D(GL_TEXTURE_2D, 0, width, 0, width, height, GL_RGBA, GL_UNSIGNED_BYTE, decoded_texture2->data.get());
glPixelStorei(GL_UNPACK_ROW_LENGTH, 0);
glBindTexture(GL_TEXTURE_2D, 0);

flipTextureVertically(tex, width, height);

return tex;
}

