                .flatMap(doctorRepository::findById)
                .ifPresent(doctorSchedule::setDoctor);

        Optional.ofNullable(dto.getRoom_id())
                .flatMap(roomRepository::findById)
                .ifPresent(doctorSchedule::setRoom);

