            foreach (AxleInfo axleInfo in axleInfos) {
                axleInfo.leftWheel.brakeTorque = brakeTorque;
                axleInfo.rightWheel.brakeTorque = brakeTorque;
            }
        } else {
            foreach (AxleInfo axleInfo in axleInfos) {
                axleInfo.leftWheel.brakeTorque = 0;
                axleInfo.rightWheel.brakeTorque = 0;
            }
