                3 => Mathf.RoundToInt((baseUnitData.BaseUnitArmor + upgradeValue) * 3),
                4 => Mathf.RoundToInt((baseUnitData.BaseUnitArmor + upgradeValue) * 4),
                5 => Mathf.RoundToInt((baseUnitData.BaseUnitArmor + upgradeValue) * 5),
