import java.io.*;
import java.util.ArrayList;
import java.util.List;

public class Management {
    List<User> users;
    List<Cardio> cardios;
    List<StrengthWorkout> strengthWorkouts;
    CaloriesTracker caloriesTracker;
    WeightTracker weightTracker;


    public Management() {
        this.users = new ArrayList<>();
        this.cardios = new ArrayList<>();
        this.strengthWorkouts = new ArrayList<>();
        this.caloriesTracker = new CaloriesTracker();
        this.weightTracker = new WeightTracker();
    }

    public boolean login(String username, String password) {
        for (User user : users) {
            if (user.getUsername().equals(username) && user.getPassword().equals(password)) {
                return true;
            }
        }
        return false;
    }

    public void register(User user) {
        users.add(user);
    }

    public List<Cardio> getCardios() {
        return cardios;
    }

    public void addCardio(User user, Cardio cardio) {
        user.getCardios().add(cardio);
    }

    public void removeCardio(User user, Cardio cardio) {
        user.getCardios().remove(cardio);
    }

    public List<StrengthWorkout> getStrengthWorkouts() {
        return strengthWorkouts;
    }

    public void addStrengthWorkout(User user, StrengthWorkout workout) {
        user.getStrengthWorkouts().add(workout);
    }

    public void removeStrengthWorkout(User user, StrengthWorkout workout) {
        user.getStrengthWorkouts().remove(workout);
    }

    public List<CalorieEntry> getCalorieEntries() {
        return caloriesTracker.getCalorieEntries();
    }

    public void addFoodCalories(User user, CalorieEntry entry) {
        user.getCalorieEntries().add(entry);
    }

    public List<WeightEntry> getWeightEntries() {
        return weightTracker.getWeightEntries();
    }

