        user.getWeightEntries().add(entry);
    }

    @SuppressWarnings("unchecked")
    public void loadUsers() {
        try (ObjectInputStream ois = new ObjectInputStream(new FileInputStream("User.DAT"))) {
            users = (List<User>) ois.readObject();
        } catch (IOException | ClassNotFoundException e) {
            e.printStackTrace();
        }
    }

    public void saveUsers() {
        try (ObjectOutputStream oos = new ObjectOutputStream(new FileOutputStream("User.DAT"))) {
            oos.writeObject(users);
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void loadCardios() {
        try (BufferedReader reader = new BufferedReader(new FileReader("Cardio.txt"))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                String name = parts[0];
                int duration = Integer.parseInt(parts[1]);
                int intensity = Integer.parseInt(parts[2]);
                String date = parts[3];
                cardios.add(new Cardio(name, duration, intensity, date));
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void saveCardios() {
        try (BufferedWriter writer = new BufferedWriter(new FileWriter("Cardio.txt"))) {
            for (Cardio cardio : cardios) {
                writer.write(cardio.getName() + "," + cardio.getDuration() + "," + cardio.getIntensity() + "," + cardio.getDate());
                writer.newLine();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void loadStrengthWorkouts() {
        try (BufferedReader reader = new BufferedReader(new FileReader("Strength.txt"))) {
            String line;
            while ((line = reader.readLine()) != null) {
                String[] parts = line.split(",");
                String name = parts[0];
                int weightLifted = Integer.parseInt(parts[1]);
                int duration = Integer.parseInt(parts[2]);
                String date = parts[3];
                strengthWorkouts.add(new StrengthWorkout(name, weightLifted, duration, date));
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

    public void saveStrengthWorkouts() {
        try (BufferedWriter writer = new BufferedWriter(new FileWriter("Strength.txt"))) {
            for (StrengthWorkout workout : strengthWorkouts) {
                writer.write(workout.getName() + "," + workout.getWeightLifted() + "," + workout.getDuration() + "," + workout.getDate());
                writer.newLine();
            }
        } catch (IOException e) {
            e.printStackTrace();
        }
    }

