    void GetDescendantsWithParticlesRecursive(GameObject obj, List<GameObject> list, int depth)
    {
        if (depth > maxDepth || depth > 30)
        {
            return; // 检查最大深度和额外的深度限制
        }

        ParticleSystem particleSystem = obj.GetComponent<ParticleSystem>();
        if (particleSystem != null)
        {
            list.Add(obj);
        }

        foreach (Transform child in obj.transform)
        {
            GetDescendantsWithParticlesRecursive(child.gameObject, list, depth + 1);
        }
    }
    void ApplySharedMaterial()
    {
        foreach (var obj in selectedObjects)
        {
            Renderer renderer = obj.GetComponent<Renderer>();
            if (renderer != null)
            {
                Undo.RecordObject(renderer, "變更共用材質");
                renderer.sharedMaterial = sharedMaterial;
                if (sharedMaterial != null)
                {
                    renderer.sharedMaterial.mainTexture = sharedMaterial.mainTexture;
                }
            }
        }
        SyncSelectedObjects();
    }

    void ApplyMaterialToSelectedObjects(Material newMaterial, int index)
    {
        if (index >= 0 && index < lastValidSelectedObjects.Length)
        {
            GameObject obj = lastValidSelectedObjects[index];
            Renderer renderer = obj.GetComponent<Renderer>();
            if (renderer != null)
            {
                Undo.RecordObject(renderer, "變更材質");
                renderer.sharedMaterial = newMaterial;
                lastValidMaterials[index] = newMaterial;
                EditorUtility.SetDirty(obj);
            }

            // 使用 SyncSelectedObjects 來更新和刷新
            SyncSelectedObjects();
        }
    }

    void ApplyTextureToSelectedObjects(Texture newTexture, int index)
    {
        if (index >= 0 && index < lastValidSelectedObjects.Length)
        {
            GameObject obj = lastValidSelectedObjects[index];
            Renderer renderer = obj.GetComponent<Renderer>();
            if (renderer != null && renderer.sharedMaterial != null)
            {
                Material mat = renderer.sharedMaterial;
                Undo.RecordObject(mat, "變更貼圖");
                mat.mainTexture = newTexture;
                lastValidTextures[index] = newTexture;
                EditorUtility.SetDirty(mat);
            }

            // 使用 SyncSelectedObjects 來更新和刷新
            SyncSelectedObjects();
        }
    }

