                break;
        }

        foreach (var obj in selectedObjects)
        {
            Renderer renderer = obj.GetComponent<Renderer>();
            if (renderer != null && renderer.sharedMaterial != null)
            {
                Undo.RecordObject(renderer.sharedMaterial, "變更 Render Queue 預設");
                if (renderQueueValue != -1)
                {
                    renderer.sharedMaterial.renderQueue = renderQueueValue;
                }
            }
        }
        SyncSelectedObjects();
    }

    void SyncSelectedObjects()
    {
        if (this == null) return;

        EditorApplication.delayCall += () =>
        {
            if (this != null)
            {
                UpdateSelectionData();
                Repaint();
            }
        };
    }
