// lib/HybridRecommendationSystem.ts
import * as tf from "@tensorflow/tfjs";
import Product from "@/lib/models/Product";
import UserInteraction from "@/lib/models/UserInteraction";

interface Item {
  id: string;
  categories: string[];
  [key: string]: any;
}

class HybridRecommendationSystem {
  private itemMap: Map<string, Item> = new Map();
  private model: tf.LayersModel | null = null;
  public isInitialized: boolean = false;
  private categoryEncoder: Map<string, number> = new Map();
  private numCategories: number;
  private itemPopularity: Map<string, number> = new Map();

  constructor(
    private numVisitors: number,
    private numItems: number,
    initialNumCategories: number,
    private epochs: number = 20,
    private diversityFactor: number = 0.2
  ) {
    this.numCategories = initialNumCategories;
    console.log(
      `Initializing HybridRecommendationSystem with ${numVisitors} visitors, ${numItems} items, and ${this.numCategories} categories`
    );
  }

  private clearExistingVariables(): void {
    try {
      tf.disposeVariables();
      console.log("Existing TensorFlow variables cleared");
    } catch (error) {
      console.error("Error clearing existing variables:", error);
    }
  }

  async initialize(): Promise<void> {
    this.clearExistingVariables();

    if (this.isInitialized) {
      console.log("Recommender system already initialized");
      return;
    }

    try {
      const visitorInput = tf.input({ shape: [1], name: "visitor_input" });
      const itemInput = tf.input({ shape: [1], name: "item_input" });
      const categoryInput = tf.input({
        shape: [this.numCategories],
        name: "category_input",
      });

      const visitorEmbedding = tf.layers
        .embedding({ inputDim: this.numVisitors, outputDim: 50 })
        .apply(visitorInput) as tf.SymbolicTensor;
      const itemEmbedding = tf.layers
        .embedding({ inputDim: this.numItems, outputDim: 50 })
        .apply(itemInput) as tf.SymbolicTensor;

      const visitorFlat = tf.layers
        .flatten()
        .apply(visitorEmbedding) as tf.SymbolicTensor;
      const itemFlat = tf.layers
        .flatten()
        .apply(itemEmbedding) as tf.SymbolicTensor;

      const concat = tf.layers
        .concatenate()
        .apply([visitorFlat, itemFlat, categoryInput]) as tf.SymbolicTensor;

      const dense1 = tf.layers
        .dense({ units: 128, activation: "relu" })
        .apply(concat) as tf.SymbolicTensor;
      const dropout1 = tf.layers
        .dropout({ rate: 0.3 })
        .apply(dense1) as tf.SymbolicTensor;
      const dense2 = tf.layers
        .dense({ units: 64, activation: "relu" })
        .apply(dropout1) as tf.SymbolicTensor;
      const dropout2 = tf.layers
        .dropout({ rate: 0.3 })
        .apply(dense2) as tf.SymbolicTensor;
      const output = tf.layers
        .dense({ units: 1 })
        .apply(dropout2) as tf.SymbolicTensor;

      this.model = tf.model({
        inputs: [visitorInput, itemInput, categoryInput],
        outputs: output,
      });
      this.model.compile({ optimizer: "adam", loss: "meanSquaredError" });
      this.isInitialized = true;
      console.log("Recommender system initialized successfully");
    } catch (error) {
      console.error("Error initializing model:", error);
      throw error;
    }
  }

  async train(
    interactions: Array<{
      visitorId: string;
      itemId: string;
      rating: number;
    }>
  ): Promise<void> {
    try {
      if (!this.isInitialized) {
        console.log("Recommender not initialized. Initializing now...");
        await this.initialize();
      }

      console.log(`Training with ${interactions.length} interactions`);

      const visitorIds = interactions.map((i) => Number(i.visitorId));
      const itemIds = interactions.map((i) => Number(i.itemId));
      const ratings = interactions.map((i) => i.rating);

      const categoryTensors = itemIds.map((id) => {
        const item = this.itemMap.get(id.toString());
        if (!item) return new Array(this.numCategories).fill(0);
        return item.categories.map((cat) => this.categoryEncoder.get(cat) || 0);
      });

      const categoryTensor = tf.tensor2d(categoryTensors);

      const tensorX = [
        tf.tensor2d(visitorIds, [visitorIds.length, 1]),
        tf.tensor2d(itemIds, [itemIds.length, 1]),
        categoryTensor,
      ];
      const tensorY = tf.tensor2d(ratings, [ratings.length, 1]);

      // Update item popularity
      interactions.forEach(({ itemId }) => {
        this.itemPopularity.set(
          itemId,
          (this.itemPopularity.get(itemId) || 0) + 1
        );
      });

      // Normalize item popularity
      const maxPopularity = Math.max(...Array.from(this.itemPopularity.values()));
      this.itemPopularity.forEach((value, key) => {
        this.itemPopularity.set(key, value / maxPopularity);
      });

      console.log("Training model...");
      await this.model!.fit(tensorX, tensorY, {
        epochs: this.epochs,
        batchSize: 32,
        shuffle: true,
        validationSplit: 0.1,
        callbacks: {
          onEpochEnd: (epoch, logs) => {
            console.log(
              `Epoch ${epoch + 1}/${this.epochs} - loss: ${logs?.loss?.toFixed(
                4
              )}`
            );
          },
        },
      });

      console.log("Model training completed");
    } catch (error) {
      console.error("Error during training:", error);
      throw error;
    }
  }

  async getRecommendations(
    visitorId: string,
    limit: number = 5
  ): Promise<string[]> {
    try {
      if (!this.isInitialized) {
        console.log("Recommender not initialized. Initializing now...");
        await this.initialize();
      }

      const itemIds = Array.from(this.itemMap.keys());
      if (itemIds.length === 0) {
        console.warn("No items available for recommendations");
        return [];
      }

      console.log(
        `Generating recommendations for visitor ${visitorId} with ${itemIds.length} items`
      );

      const visitorIdTensor = tf.tensor2d(
        new Array(itemIds.length).fill(Number(visitorId)),
        [itemIds.length, 1]
      );
      const itemIdTensor = tf.tensor2d(
        itemIds.map((id) => Number(id)),
        [itemIds.length, 1]
      );

      const categoryTensors = itemIds.map((id) => {
        const item = this.itemMap.get(id);
        if (!item) return new Array(this.numCategories).fill(0);
        return item.categories.reduce((acc, cat) => {
          const index = this.categoryEncoder.get(cat);
          if (index !== undefined) acc[index] = 1;
          return acc;
        }, new Array(this.numCategories).fill(0));
      });

      const categoryTensor = tf.tensor2d(categoryTensors);

      const predictions = this.model!.predict([
        visitorIdTensor,
        itemIdTensor,
        categoryTensor,
      ]) as tf.Tensor;

      const predictionsArray = (await predictions.array()) as number[][];

      // Adjust predictions based on popularity and diversity
      const adjustedPredictions = predictionsArray.map((pred, index) => ({
        itemId: itemIds[index],
        score:
          pred[0] * (1 - this.diversityFactor) +
          (1 - (this.itemPopularity.get(itemIds[index]) || 0)) *
            this.diversityFactor,
      }));

      // Sort by adjusted score and get top recommendations
      adjustedPredictions.sort((a, b) => b.score - a.score);
      let recommendedItems = adjustedPredictions
        .slice(0, limit)
        .map((item) => item.itemId);

      console.log(
        `Generated ${recommendedItems.length} recommendations for visitor ${visitorId}`
      );

      visitorIdTensor.dispose();
      itemIdTensor.dispose();
      categoryTensor.dispose();
      predictions.dispose();

      return recommendedItems;
    } catch (error) {
      console.error("Error generating recommendations:", error);
      throw error;
    }
  }

  setItems(items: Item[]): void {
    try {
      this.itemMap.clear();
      const allCategories = new Set<string>();
      for (const item of items) {
        this.itemMap.set(item.id, item);
        item.categories.forEach((cat) => allCategories.add(cat));
      }
      this.categoryEncoder = new Map(
        Array.from(allCategories).map((cat, index) => [cat, index])
      );
      this.numCategories = allCategories.size;
      console.log(
        `Set ${items.length} items and ${this.numCategories} categories in the recommender`
      );

      // Reinitialize the model if the number of categories has changed
      if (
        this.isInitialized &&
        this.numCategories !== this.model!.inputs[2].shape![1]
      ) {
        console.log("Number of categories changed. Reinitializing model...");
        this.isInitialized = false;
        this.initialize();
      }
    } catch (error) {
      console.error("Error setting items:", error);
      throw error;
    }
  }
}

