      { label: "Quality of individual data", model: "qualityIndividualData" },
      { label: "Group transformation and statistical analysis", model: "groupTransfStatisticalAnalysis" },
      { label: "Quality of data usage", model: "qualityDataUsage" },
      { label: "Duplicate search", model: "duplicateSearch" },
      { label: "Duplicate search with similarity", model: "duplicateSearchSimilarity" },
    ];

    const toggleSidebar = () => {
      isSidebarVisible.value = !isSidebarVisible.value;
    };

    const changeTab = (numTab) => {
      currentTab.value = numTab;
    };

    const fetchColumnNames = async () => {
      console.log("Selected table:", tableFunc.currentTable);
      await loadTableStructure();
      await loadTableData();
    };

    const loadTableStructure = async () => {
      loading.value = true;
      try {
        const response = await axios.get(`/api/table/${tableFunc.currentTable}/structure`);
        headers.value = response.data.columns;
        console.log("Headers:", headers.value);
      } catch (error) {
        console.error("Errore nel caricamento della struttura della tabella:", error);
      } finally {
        loading.value = false;
      }
    };

    const loadTableData = async () => {
      loading.value = true;
      statistics.value = {};

      try {
        // Fetch data in blocks
        for (let offset = 0; ; offset += BLOCK_SIZE) {
          const response = await axios.get(`/api/table/${tableFunc.currentTable}/data`, {
            params: { offset },
          });
          const { data } = response;

          if (!data || data.length === 0) break;

          // Update df with new data
          if (!df.value) {
            df.value = new dfd.DataFrame(data);
          } else {
            const newDf = new dfd.DataFrame(data);
            df.value = dfd.concat({ dfList: [df.value, newDf], axis: 0 });
          }
        }
      } catch (error) {
        console.error("Errore nel caricamento dei dati della tabella:", error);
      } finally {
        loading.value = false;
      }
    };

    const applyFilters = async () => {
      try {
        const response = await axios.post(`/api/applyFilter/${tableFunc.currentTable}`, filters.value);
        console.log(response.data);
      } catch (error) {
        console.error("Errore nell'applicazione dei filtri:", error);
      }
    };

    onMounted(() => {
      fetchColumnNames();
    });

    return {
      tableFunc,
      isSidebarVisible,
      currentTab,
      loading,
      dataMap,
      headers,
      rowCount,
      statistics,
      filters,
      filterOptions,
      toggleSidebar,
      changeTab,
      fetchColumnNames,
      applyFilters,
    };
  },
  methods: {
    goBack() {
      this.$emit("goBack");
    },
  }
};
