  <script setup>
  import { ref, onMounted, watch, computed } from 'vue';

  const props = defineProps({
    tableName: {
      type: String,
      required: true
    }
  });

  const containerRef = ref(null);
  const canvasRef = ref(null);
  const tableData = ref([]);
  const columns = ref([]);
  const scale = ref(1);
  const offset = ref({ x: 0, y: 0 });

  const CELL_WIDTH = 150;
  const CELL_HEIGHT = 30;
  const PADDING = 5;

  const canvasWidth = computed(() => window.innerWidth);
  const canvasHeight = computed(() => window.innerHeight);

  const totalWidth = computed(() => columns.value.length * CELL_WIDTH * scale.value);
  const totalHeight = computed(() => (tableData.value.length + 1) * CELL_HEIGHT * scale.value);

  const fetchData = async () => {
    try {
      const response = await fetch(`/api/table/${props.tableName}/data`);
      if (!response.ok) throw new Error('Network response was not ok');
      const data = await response.json();
      tableData.value = data;
      columns.value = Object.keys(data[0] || {});
      drawTable();
    } catch (error) {
      console.error("Error fetching data:", error);
    }
  };

  const drawTable = () => {
    if (!canvasRef.value || !tableData.value.length) return;
    const canvas = canvasRef.value;
    const ctx = canvas.getContext('2d');

    canvas.width = canvasWidth.value;
    canvas.height = canvasHeight.value;

    ctx.clearRect(0, 0, canvas.width, canvas.height);
    ctx.save();
    ctx.scale(scale.value, scale.value);
    ctx.translate(offset.value.x, offset.value.y);

    // Draw header
    ctx.fillStyle = '#f0f0f0';
    ctx.fillRect(0, 0, totalWidth.value, CELL_HEIGHT);
    ctx.fillStyle = '#000000';
    ctx.font = 'bold 12px Arial';
    columns.value.forEach((col, index) => {
      const x = index * CELL_WIDTH;
      ctx.fillRect(x, 0, CELL_WIDTH, CELL_HEIGHT);
      ctx.fillStyle = '#000000';
      ctx.fillText(col, x + PADDING, CELL_HEIGHT / 2 + PADDING);
    });

    // Draw data
    ctx.font = '12px Arial';
    tableData.value.forEach((row, rowIndex) => {
      columns.value.forEach((col, colIndex) => {
        const x = colIndex * CELL_WIDTH;
        const y = (rowIndex + 1) * CELL_HEIGHT;

        ctx.fillStyle = '#ffffff';
        ctx.fillRect(x, y, CELL_WIDTH, CELL_HEIGHT);

        ctx.fillStyle = '#000000';
        ctx.strokeRect(x, y, CELL_WIDTH, CELL_HEIGHT);

        const text = String(row[col]);
        ctx.fillText(text, x + PADDING, y + CELL_HEIGHT / 2 + PADDING);
      });
    });

    ctx.restore();
  };

  const handleMouseMove = (event) => {
    const canvas = canvasRef.value;
    const rect = canvas.getBoundingClientRect();
    const x = (event.clientX - rect.left - offset.value.x * scale.value) / scale.value;
    const y = (event.clientY - rect.top - offset.value.y * scale.value) / scale.value;

    const col = Math.floor(x / CELL_WIDTH);
    const row = Math.floor(y / CELL_HEIGHT) - 1;

    if (row >= 0 && row < tableData.value.length && col >= 0 && col < columns.value.length) {
      const cellData = tableData.value[row][columns.value[col]];
      // Display tooltip or update UI with cell data
      console.log(`Cell data: ${cellData}`);
    }
  };

  const handleWheel = (event) => {
    event.preventDefault();
    const delta = event.deltaY > 0 ? 0.9 : 1.1;
    scale.value *= delta;
    scale.value = Math.max(0.1, Math.min(scale.value, 5)); // Limit zoom level

    // Adjust offset to keep the mouse position fixed during zoom
    const rect = canvasRef.value.getBoundingClientRect();
    const mouseX = event.clientX - rect.left;
    const mouseY = event.clientY - rect.top;

    offset.value.x -= (mouseX / scale.value) * (1 - 1 / delta);
    offset.value.y -= (mouseY / scale.value) * (1 - 1 / delta);

    drawTable();
  };

  watch(() => props.tableName, fetchData);
  watch([tableData, columns, scale, offset], drawTable);

  onMounted(() => {
    fetchData();
    window.addEventListener('resize', () => {
      canvasRef.value.width = window.innerWidth;
      canvasRef.value.height = window.innerHeight;
      drawTable();
    });
  });
