// Вспомогательная функция для рендеринга шаблона
func renderTemplate(w http.ResponseWriter, templateName string, data interface{}) {
	funcMap := template.FuncMap{
		"formatPrice": func(price float64) string {
			return strconv.FormatFloat(price, 'f', 2, 64)
		},
		"multiply": func(price float64, quantity int) float64 {
			return price / 100 * float64(quantity)
		},
	}

	tmpl, err := template.New("").Funcs(funcMap).ParseFiles(
		"templates/template.html",
		"templates/search.html",
		"templates/content.html",
		"templates/footer.html",
		"templates/product_detail.html",
		"templates/cart.html",
	)
	if err != nil {
		log.Println("Ошибка загрузки шаблона: ", err)
		http.Error(w, "Ошибка загрузки шаблона", http.StatusInternalServerError)
		return
	}

	err = tmpl.ExecuteTemplate(w, templateName, data)
	if err != nil {
		log.Println("Ошибка выполнения шаблона: ", err)
		http.Error(w, "Ошибка выполнения шаблона", http.StatusInternalServerError)
	}
}

