func addToCartHandler(w http.ResponseWriter, r *http.Request) {
	var requestData struct {
		ProductID string `json:"productID"`
		Quantity  int    `json:"quantity"`
	}

	if err := json.NewDecoder(r.Body).Decode(&requestData); err != nil {
		http.Error(w, "Invalid request payload", http.StatusBadRequest)
		return
	}

	token := "63342e403e952c4b2ffeaf47705bd88ff7323aee"

	product, err := getProductByIDFromMoySklad(token, requestData.ProductID)
	if err != nil {
		http.Error(w, fmt.Sprintf("Ошибка получения данных из 'Мой Склад': %v", err), http.StatusInternalServerError)
		return
	}

	userCart.Mutex.Lock()
	defer userCart.Mutex.Unlock()

	if item, exists := userCart.Items[requestData.ProductID]; exists {
		if item.Quantity+requestData.Quantity > int(product.Quantity) {
			http.Error(w, "Not enough stock", http.StatusBadRequest)
			return
		}
		item.Quantity += requestData.Quantity
		userCart.Items[requestData.ProductID] = item
	} else {
		if requestData.Quantity > int(product.Quantity) {
			http.Error(w, "Not enough stock", http.StatusBadRequest)
			return
		}
		userCart.Items[requestData.ProductID] = CartItem{
			Product:  *product,
			Quantity: requestData.Quantity,
		}
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(map[string]bool{"success": true})
}

func updateCartQuantityHandler(w http.ResponseWriter, r *http.Request) {
	var requestData struct {
		ProductID string `json:"productID"`
		Change    int    `json:"change"`
	}

	if err := json.NewDecoder(r.Body).Decode(&requestData); err != nil {
		http.Error(w, "Invalid request payload", http.StatusBadRequest)
		return
	}

	token := "63342e403e952c4b2ffeaf47705bd88ff7323aee"

	product, err := getProductByIDFromMoySklad(token, requestData.ProductID)
	if err != nil {
		http.Error(w, fmt.Sprintf("Ошибка получения данных из 'Мой Склад': %v", err), http.StatusInternalServerError)
		return
	}

	userCart.Mutex.Lock()
	defer userCart.Mutex.Unlock()

	if item, exists := userCart.Items[requestData.ProductID]; exists {
		newQuantity := item.Quantity + requestData.Change
		if newQuantity > int(product.Quantity) {
			http.Error(w, "Not enough stock", http.StatusBadRequest)
			return
		}
		if newQuantity <= 0 {
			delete(userCart.Items, requestData.ProductID)
		} else {
			item.Quantity = newQuantity
			userCart.Items[requestData.ProductID] = item
		}
	} else if requestData.Change > 0 {
		if requestData.Change > int(product.Quantity) {
			http.Error(w, "Not enough stock", http.StatusBadRequest)
			return
		}
		userCart.Items[requestData.ProductID] = CartItem{
			Product:  *product,
			Quantity: requestData.Change,
		}
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(map[string]bool{"success": true})
}

// Обработчик для отображения корзины
func cartHandler(w http.ResponseWriter, r *http.Request) {
	userCart.Mutex.Lock()
	defer userCart.Mutex.Unlock()

	totalPrice := 0.0
	totalQuantity := 0
	for _, item := range userCart.Items {
		price := 0.0
		if len(item.Product.SalePrices) > 0 {
			price = item.Product.SalePrices[0].Value
		}
		totalPrice += float64(item.Quantity) * price / 100
		totalQuantity += item.Quantity
	}

	tmplData := CartTemplateData{
		Items:         userCart.Items,
		TotalPrice:    totalPrice,
		TotalQuantity: totalQuantity,
	}

	renderTemplate(w, "cart.html", tmplData)
}

