	userCart.Mutex.Lock()
	defer userCart.Mutex.Unlock()

	if _, exists := userCart.Items[requestData.ProductID]; exists {
		delete(userCart.Items, requestData.ProductID)
	}

	w.Header().Set("Content-Type", "application/json")
	json.NewEncoder(w).Encode(map[string]bool{"success": true})
}
