    to: schedule.workTime.to || "",
    isAllDay: false,
  });
  const [breakTimes, setBreakTimes] = useState<ITimeState[]>(schedule.breaksTime || [{ from: "", to: "" }]);

  const prevScheduleRef = useRef<ISchedule | null>(null);

  useEffect(() => {
    const schedule: ISchedule = {
      dayOfWeek: selectedDays,
      workTime: {
        from: workTime.from,
        to: workTime.to,
      },
      breaksTime: breakTimes,
    };

    if (JSON.stringify(prevScheduleRef.current) !== JSON.stringify(schedule)) {
      prevScheduleRef.current = schedule;
      onScheduleChange(schedule);
    }
  }, [selectedDays, workTime, breakTimes, onScheduleChange]);

  /**
   * Форматирует строку времени в формат HH:MM.
   */
  const formatTime = useCallback((time: string): string => {
    const cleaned = time.replace(/[^0-9]/g, "");
    const length = cleaned.length;
    if (length <= 2) return cleaned;
    return cleaned.slice(0, 2) + ":" + cleaned.slice(2, 4);
  }, []);

  /**
   * Обработчик изменения времени работы.
   */
  const handleTimeChange = useCallback(
    (field: keyof ITimeState) => (e: ChangeEvent<HTMLInputElement>) => {
      const formattedTime = formatTime(e.target.value);
      setWorkTime((prevState) => ({ ...prevState, [field]: formattedTime }));
    },
    [formatTime],
  );

  /**
   * Обработчик изменения состояния "весь день".
   */
  const handleAllDayChange = useCallback((e: ChangeEvent<HTMLInputElement>) => {
    const checked = e.target.checked;
    setWorkTime({
      from: "00:00",
      to: "23:59",
      isAllDay: checked,
    });
  }, []);

  /**
   * Обработчик изменения времени перерыва.
   */
  const handleBreakTimeChange = useCallback(
    (index: number, field: keyof ITimeState) => (e: ChangeEvent<HTMLInputElement>) => {
      const formattedTime = formatTime(e.target.value);
      setBreakTimes((prevState) => {
        const newBreakTimes = [...prevState];
        newBreakTimes[index] = { ...newBreakTimes[index], [field]: formattedTime };
