      });
    },
    [formatTime],
  );

  /**
   * Добавляет новый перерыв.
   */
  const addBreak = useCallback(() => {
    setBreakTimes((prevState) => [...prevState, { from: "", to: "" }]);
  }, []);

  /**
   * Удаляет последний перерыв.
   */
  const removeLastBreak = useCallback(() => {
    setBreakTimes((prevState) => prevState.slice(0, -1));
  }, []);

  /**
   * Проверяет, заполнены ли поля времени работы.
   */
  const isTimeInputComplete = useCallback(() => {
    const { from, to, isAllDay } = workTime;
    return isAllDay || (from.length === 5 && to.length === 5);
