              <Trash style={{ fill: "Red" }} />
            </Button>
          )}
        </LocalFieldInputContainer>
      </LocalRowContainer>
      {selectedDays.length > 0 && (
        <TimeInput name={schedules.workTime[${index}]} workTime={workTime} handleTimeChange={handleTimeChange} handleAllDayChange={handleAllDayChange} />
      )}
      {selectedDays.length > 0 && isTimeInputComplete() && (
        <>
          {breakTimes.map((breakTime, idx) => (
            <BreaksInput
              key={idx}
              name={schedules.breaksTime[${idx}]}
              breakTime={breakTime}
              handleBreakTimeChange={handleBreakTimeChange.bind(null, index)}
              isLast={idx === breakTimes.length - 1 && breakTimes.length > 1}
              removeLastBreak={removeLastBreak}
              index={idx}
            />
          ))}
          <Button variant="tertiary" shape="rounded" onClick={addBreak} size={"small"} sx={LocalAddButton}>
            {t("pages.officesAtms.addOffice.officeSchedule.buttons.addBreak")}
          </Button>
        </>
      )}
    </>
  );
};

