interface DaysSelectorProps {
  name: string;
  selectedDays: number[];
  setSelectedDays: React.Dispatch<React.SetStateAction<number[]>>;
}

const DaysSelector: React.FC<DaysSelectorProps> = ({ name, selectedDays, setSelectedDays }) => {
  const { t } = useTranslation();
  const { control, formState: { errors }, setValue, getValues } = useFormContext();

  const DAYS_OF_WEEK_FULL: string[] = t("pages.officesAtms.daysOfWeek", { returnObjects: true });
  const DAYS_OF_WEEK_SHORT: string[] = t("pages.officesAtms.daysOfWeekShort", { returnObjects: true });
  const DAILY = t("pages.officesAtms.addOffice.officeSchedule.daily");

  const handleChange = (event: SelectChangeEvent<unknown>) => {
    const value = event.target.value as string[];
    if (value.includes(DAILY)) {
      if (selectedDays.length < 7) {
        setSelectedDays([0, 1, 2, 3, 4, 5, 6]);
      } else {
        const indexToRemove = DAYS_OF_WEEK_SHORT.indexOf(value[value.length - 1]);
        setSelectedDays(selectedDays.filter((day) => day !== indexToRemove));
      }
    } else {
      const mappedDays = value.map((day) => DAYS_OF_WEEK_SHORT.indexOf(day));
      setSelectedDays(mappedDays);
    }
    setValue(name, selectedDays);
  };

  return (
    <LocalFieldContainer>
      <LocalFieldLabelSchedule>
        <LocalLabelText>{t("pages.officesAtms.addOffice.officeSchedule.daysOfWeekLabel")}</LocalLabelText>
      </LocalFieldLabelSchedule>
      <LocalFormControl error={!!errors[name]}>
        <Controller
          name={name}
          control={control}
          render={({ field }) => (
            <>
              <LocalSelect
                error={!!errors[name]}
                multiple
                required
                value={selectedDays.length === 7 ? [DAILY] : selectedDays.map((day) => DAYS_OF_WEEK_SHORT[day])}
                onChange={(event) => {
                  handleChange(event);
                  field.onChange(event);
                }}
                input={<OutlinedInput />}
                sx={{ width: "256px" }}
                IconComponent={ArrowDropDown}
                renderValue={(value: unknown) => {
                  const selected = value as string[];
                  return selected.length === 7 ? DAILY : selected.join(", ");
                }}
                MenuProps={{
                  PaperProps: {
                    style: {
                      boxShadow: "0px 6px 12px 0px rgba(4, 9, 33, 0.08)",
                      border: "1px solid rgba(24, 27, 33, 0.08)",
                      borderRadius: "16px",
                      marginTop: "6px",
                      width: "256px",
                    },
                  },
                }}
              >
                <LocalMenuItem value={DAILY}>
                  <Checkbox checked={selectedDays.length === 7} />
                  <ListItemText
                    primaryTypographyProps={{ fontSize: "14px", fontFamily: "CoFoSans-Regular" }}
                    primary={DAILY}
                  />
                </LocalMenuItem>
                {DAYS_OF_WEEK_FULL.map((name, index) => (
                  <LocalMenuItem key={name} value={DAYS_OF_WEEK_SHORT[index]}>
                    <Checkbox checked={selectedDays.includes(index)} />
                    <ListItemText
                      primaryTypographyProps={{ fontSize: "14px", fontFamily: "CoFoSans-Regular" }}
                      primary={name}
                    />
                  </LocalMenuItem>
                ))}
              </LocalSelect>
              {errors[name] && (
                <FormHelperText sx={{ fontSize: "12px", fontFamily: "CoFoSans-Regular", marginLeft: "0" }}>
                  {errors[name]?.message as string}
                </FormHelperText>
              )}
              <Button onClick={() => {
