function getCarInfo(x, y, orientation) {
  let length = 1;
  let top = y;
  let left = x;
  const direction = orientation === 1 ? [0, 1] : [1, 0];
  let [dx, dy] = direction;
  let [nx, ny] = [x - dx, y - dy];

  while (ny >= 0 && nx >= 0 && gameState[ny][nx][0] === gameState[y][x][0]) {
    length++;
    top = ny;
    left = nx;
    nx -= dx;
    ny -= dy;
  }

  [nx, ny] = [x + dx, y + dy];
  while (ny < gameState.length && nx < gameState[0].length && gameState[ny][nx][0] === gameState[y][x][0]) {
    length++;
    nx += dx;
    ny += dy;
  }

  return [left, top, length];
}

function checkValidMove(carX, carY, newPosition, moveAxis, carLength) {
  if ((moveAxis === 'x' && (newPosition < 0 || newPosition + carLength > gameState[0].length)) ||
