async fn generate_and_save_addresses(
    thread_id: usize,
    redis_client: Arc<Client>,
    file_sender: mpsc::Sender<Vec<(String, String)>>,
                                     crypto_pool: Arc<rayon::ThreadPool>,
                                     semaphore: Arc<Semaphore>,
) -> Result<(), Box<dyn std::error::Error + Send + Sync>> {
    let mut total_generated = 0;
    let mut redis_conn = redis_client.get_connection()?;

    let file_name = format!("all_addresses_{}.txt", thread_id);
    let mut file = BufWriter::new(File::create(&file_name).await?);

    loop {
        let mut addresses = Vec::with_capacity(BATCH_LOOKUP_SIZE);

        for _ in 0..BATCH_LOOKUP_SIZE {
            let mut rng = OsRng;
            let mut private_key = [0u8; 32];
            rng.fill_bytes(&mut private_key);

            let secret_key = SecretKey::from_slice(&private_key).expect("32 bytes, within curve order");
            let public_key = PublicKey::from_secret_key(&Secp256k1::new(), &secret_key);
            let public_key_bytes = &public_key.serialize_uncompressed()[1..];

            let mut hasher = Keccak256::new();
            hasher.update(public_key_bytes);
            let public_key_hash = hasher.finalize();

            let public_key = format!("0x{}", hex::encode(&public_key_hash[12..]));
            let private_key_hex = hex::encode(private_key);

            addresses.push((public_key.clone(), private_key_hex.clone()));

            if DEBUG_SAVE {
                file.write_all(format!("{},{}\n", public_key, private_key_hex).as_bytes()).await?;
            }
        }

        let permit = semaphore.acquire().await.unwrap();
        match check_and_save_addresses(&mut redis_conn, &addresses, &file_sender).await {
            Ok(_) => {
                if DEBUG_SAVE == true {
                    println!("Addresses checked and saved successfully");
                }
            }
            Err(e) => {
                eprintln!("Error checking and saving addresses: {:?}", e);
            }
        }
        drop(permit);

        total_generated += BATCH_LOOKUP_SIZE;

        if total_generated % (BATCH_LOOKUP_SIZE * 100) == 0 {
            println!("Thread {} generated {} addresses", thread_id, total_generated);
        }

        if total_generated >= BATCH_SIZE {
            break;
        }
    }

    if DEBUG_SAVE {
        file.flush().await?;
    }

    println!("Thread {} finished. Generated {} addresses. All saved to {}", thread_id, total_generated, file_name);

    Ok(())
}

async fn check_and_save_addresses(
    redis_conn: &mut Connection,
    addresses: &[(String, String)],
                                  file_sender: &mpsc::Sender<Vec<(String, String)>>,
) -> Result<(), Box<dyn std::error::Error>> {
    let mut found_addresses = Vec::new();

    for chunk in addresses.chunks(BATCH_REDIS_SIZE) {
        let mut pipeline = redis::pipe();
        for (address, _) in chunk {
            pipeline.exists(address);
        }

        let exists_results: Vec<bool> = pipeline.query(redis_conn)?;

        for (i, (address, private_key)) in chunk.iter().enumerate() {
            if exists_results[i] {
                found_addresses.push((address.clone(), private_key.clone()));

                if found_addresses.len() >= SAVE_BATCH_SIZE {
                    if let Err(e) = file_sender.send_timeout(found_addresses.clone(), Duration::from_secs(5)).await {
                        println!("Error sending data: {:?}", e);
                    }
                    found_addresses.clear();
                }
            }
        }
    }

    if !found_addresses.is_empty() {
        if let Err(e) = file_sender.send_timeout(found_addresses, Duration::from_secs(5)).await {
            println!("Error sending data: {:?}", e);
        }
    }

    Ok(())
}
