                            val isScrollable = !(firstVisibleItem.index == 0 &&
                                    firstVisibleItem.offset == 0 &&
                                    lastVisibleItem.index + 1 == layoutInfo.totalItemsCount &&
                                    lastVisibleItem.offset + lastVisibleItem.size <= viewportHeight)

