        cancelButton.addActionListener(e -> System.exit(0));

        add(panel);
    }

    private class ExecuteButtonListener implements ActionListener {
        @Override
        public void actionPerformed(ActionEvent e) {
            String computerName = computerNameField.getText().trim();
            for (Component comp : checkBoxPanel.getComponents()) {
                if (comp instanceof JCheckBox) {
                    JCheckBox checkBox = (JCheckBox) comp;
                    if (checkBox.isSelected()) {
                        try {
                            String folderName = checkBox.getText();
                            File folder = new File("D:\\Distrib\\" + folderName);
                            String remoteInstallPath = "\\\\" + computerName + "\\c$\\temp\\" + folderName;

                            // Copy files to remote computer
