    private void copyFolder(String sourcePath, String destinationPath) throws IOException {
        Path srcPath = new File(sourcePath).toPath();
        Path destPath = new File(destinationPath).toPath();
        Files.walk(srcPath).forEach(source -> {
            try {
                Path destination = destPath.resolve(srcPath.relativize(source));
                Files.copy(source, destination, StandardCopyOption.REPLACE_EXISTING);
            } catch (IOException e) {
                e.printStackTrace();
            }
        });
    }

    private void executeRemoteInstall(String computerName, String installPath) throws IOException, InterruptedException {
