import re
from odoo import http, _
from odoo.http import request
from odoo.addons.website_sale.controllers.main import WebsiteSale

class WebsiteSaleInherit(WebsiteSale):
    @http.route(['/shop/address'], type='http', methods=['GET', 'POST'], auth="public", website=True, sitemap=False)
    def address(self, **kw):
        result = super(WebsiteSaleInherit, self).address(**kw)
        if isinstance(result, dict) and 'error' in result:
            if 'error_message' in result:
                result['error_message'] = [msg for msg in result['error_message'] if not msg.startswith("Please enter a valid")]
        return result

    def _validate_full_name(self, name):
        if '  ' in name:
            return "NAME_DOUBLE_SPACE"
        if not self._name_regex().match(name):
            return "NAME_INVALID"
        return ""

    def _validate_street(self, street):
        is_valid, message = self._validate_croatian_address(street)
        if not is_valid:
            return f"STREET_INVALID: {message}"
        return ""

    def checkout_form_validate(self, mode, all_form_values, data):
        error, error_message = super(WebsiteSaleInherit, self).checkout_form_validate(mode, all_form_values, data)

        name = all_form_values.get('name', '').strip()
        name_error = self._validate_full_name(name)
        if name_error:
            error['name'] = 'error'
            error_message.append(name_error)

        street = all_form_values.get('street', '').strip()
        street_error = self._validate_street(street)
        if street_error:
            error['street'] = 'error'
            error_message.append(street_error)

        return error, error_message

    @staticmethod
    def _name_regex():
        name_components = {
            'first_name': r'[A-ZČĆĐŠŽ][a-zčćđšž]+',
            'hyphenated': r'(-[A-ZČĆĐŠŽ][a-zčćđšž]+)?',
            'subsequent_names': r'(\s+[A-ZČĆĐŠŽ][a-zčćđšž]+(-[A-ZČĆĐŠŽ][a-zčćđšž]+)?)+',
        }
        pattern = f"^{name_components['first_name']}{name_components['hyphenated']}{name_components['subsequent_names']}$"
        return re.compile(pattern)

    @staticmethod
    def _validate_croatian_address(address):
        regex = re.compile(r"""
            ^                                  # Start of string
            [a-zA-ZčČćĆđĐšŠžŽ\s\d.,'-]+        # Street name: letters, diacritics, spaces, digits, periods, commas, apostrophes, hyphens
            ,?\s*                              # Optional comma followed by optional spaces
            (br\.\s*)?                         # Optional "br." followed by spaces
            \d+[a-zA-Z]?                       # Primary house number: digits followed by an optional letter
            (/?\d+[a-zA-Z]?)?                  # Optional secondary house number with a slash
            (-?\d+[a-zA-Z]?)?                  # Optional tertiary house number with a hyphen
            $                                  # End of string
        """, re.VERBOSE | re.IGNORECASE)

        match = regex.match(address)

        if not match:
            if not re.match(r"^[a-zA-ZčČćĆđĐšŠžŽ\s\d.,'-]+", address, re.IGNORECASE):
                return False, _("Invalid characters in street name.")
            if "br." in address.lower() and not re.search(r"br\.\s*\d", address, re.IGNORECASE):
                return False, _("Incorrectly formatted 'br.'.")
            if not re.search(r"\d", address):
                return False, _("Missing house number.")
            if re.search(r"[!@#$%^&*()_+={}[\]|;:\"<>?~`]", address):
                return False, _("Invalid special characters in address.")
            if re.search(r"\d{2,}/\d{2,}", address):
                return False, _("Too many digits around the slash in house number.")
            if re.search(r"\d{2,}-\d{2,}", address):
                return False, _("Too many digits around the hyphen in house number.")
            if re.search(r"//", address):
                return False, _("Double slashes in house number.")
            if re.search(r"--", address):
                return False, _("Double hyphens in house number.")
            return False, _("General formatting error.")

        return True, _("Valid address.")
