    <div class="container">
        <h1>QRCode - VCard Generator</h1>
        <div>
            <div>
                <input type="text" name="" id="first" placeholder="first name">
                <input type="text" name="" id="last" placeholder="last name">
                <input type="text" name="" id="job" placeholder="Job">
                <input type="text" name="" id="job_pos" placeholder="Job Position">
                <input type="tel" name="" id="cel" placeholder="Cel.">
