                <button onclick="generate()">Generate QRCode</button>
                <button onclick="generatePDF()" disabled>Generate PDF</button>
                <button onclick="generateEPS()" disabled>Generate EPS</button>
            </div>
            <canvas id="combinedCanvas" width="400" height="500"></canvas>
            <canvas id="qrcodeCanvas" width="200" height="200"></canvas>
            <canvas id="infoCanvas" width="400" height="300"></canvas>
        </div>
    </div>

    <script src="./jspdf.js"></script>
    <script src="./qrcode.js"></script>

    <script>
        function generate() {
            const first = document.querySelector("#first").value;
            const last = document.querySelector("#last").value;
            const job = document.querySelector('#job').value;
            const jobPos = document.querySelector('#job_pos').value;
            const cel = document.querySelector('#cel').value;
            const tel = document.querySelector('#tel').value;
            const email = document.querySelector('#email').value;
            const website = document.querySelector('#website').value;
            const country = document.querySelector('#country').value;
            const city = document.querySelector('#city').value;
            const state = document.querySelector('#state').value;
            const street = document.querySelector('#street').value;

            const infoCanvas = document.querySelector('#infoCanvas');
            const ctxInfo = infoCanvas.getContext('2d');
            let infoHeight = 0;

            const vCardData = `BEGIN:VCARD\nVERSION:3.0\nN:${last};${first}\nFN:${first} ${last}\nORG:${job}\nTITLE:${jobPos}\nADR:;;${street};${city};${state};;${country}\nTEL;WORK;VOICE:${tel}\nTEL;CELL:${cel}\nTEL;FAX:\nEMAIL;WORK;INTERNET:${email}\nURL:${website}\nEND:VCARD`;
            if (document.querySelector('#showName').checked) {

                ctxInfo.clearRect(0, 0, infoCanvas.width, infoCanvas.height);

                ctxInfo.font = '22px Arial';
                ctxInfo.fillStyle = 'black';

                let yPos = 30;
                const lineHeight = 30;

                ctxInfo.fillText(`Name: ${first} ${last}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Job: ${job}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Position: ${jobPos}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Cell: ${cel}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Tel: ${tel}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Email: ${email}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Website: ${website}`, 20, yPos);
                yPos += lineHeight;
                ctxInfo.fillText(`Address:`, 20, yPos);
                yPos += lineHeight;
                if(street.trim() != "" && city.trim() != ""){
                    ctxInfo.fillText(`${street}, ${city}`, 20, yPos);
                    yPos += lineHeight;
                }
                if (state.trim() != "" && city.trim() != ""){
                    ctxInfo.fillText(`${state}, ${country}`, 20, yPos);
                }

                // Calculate required height for combined canvas
                infoHeight = yPos + lineHeight;
                const qrcodeSize = 200;
                const combinedHeight = infoHeight + qrcodeSize + 100; // 20 for padding

                // Adjust combined canvas size
                const combinedCanvas = document.querySelector('#combinedCanvas');
                combinedCanvas.height = combinedHeight;
            }

            // Generate QR code on a separate canvas
            const qrcodeCanvas = document.querySelector('#qrcodeCanvas');

            QRCode.toCanvas(qrcodeCanvas, vCardData, function (error) {
                if (error) console.error(error);

                // Combine the canvases
                if (!document.querySelector('#showName').checked) {
                    qrcodeCanvas.style.display = 'block';
                    document.querySelector("#combinedCanvas").style.display = 'none';
                }else{
                    qrcodeCanvas.style.display = 'none';
                    document.querySelector("#combinedCanvas").style.display = 'block';
                    const ctxCombined = combinedCanvas.getContext('2d');
                    ctxCombined.fillStyle = 'white';
                    ctxCombined.fillRect(0, 0, combinedCanvas.width, combinedCanvas.height);
                    ctxCombined.drawImage(infoCanvas, 0, 0);
                    ctxCombined.drawImage(qrcodeCanvas, combinedCanvas.width/5, infoHeight);
                }
                document.querySelector('button:nth-of-type(2)').disabled = false;
                document.querySelector('button:nth-of-type(3)').disabled = false;
            });
        }

        function generatePDF() {
            const { jsPDF } = window.jspdf;
            const doc = new jsPDF('p', 'px', [400, 500]);
            const canvas = document.getElementById('combinedCanvas');
            const imgData = canvas.toDataURL('image/png');
            doc.addImage(imgData, 'PNG', 0, 0, 400, 500);
            doc.save('qrcode_vcard.pdf');
        }

        function generateEPS() {
            const canvas = document.getElementById('combinedCanvas');
            const ctx = canvas.getContext('2d');
            const imageData = ctx.getImageData(0, 0, canvas.width, canvas.height);
            const pixels = imageData.data;

            let eps = '%!PS-Adobe-3.0 EPSF-3.0\n';
                eps += '%%BoundingBox: 0 0 400 500\n';
                eps += '/picstr 400 string def\n';
                eps += '400 500 scale\n';
                eps += '400 500 8 [400 0 0 -500 0 500]\n';
                eps += '{currentfile picstr readhexstring pop}\n';
                eps += 'false 3 colorimage\n';

            for (let i = 0; i < pixels.length; i += 4) {
                const r = pixels[i].toString(16).padStart(2, '0');
                const g = pixels[i + 1].toString(16).padStart(2, '0');
                const b = pixels[i + 2].toString(16).padStart(2, '0');
                eps += r + g + b;
                if ((i + 4) % (400 * 4) === 0) eps += '\n';
            }

            const blob = new Blob([eps], {type: 'application/postscript'});
            const link = document.createElement('a');
            link.href = URL.createObjectURL(blob);
            link.download = 'qrcode_vcard.eps';
            link.click();
        }
    </script>
</body>
</html>


