        if (currentLength < minLength)
        {
            minLength = currentLength;
            bestOrder = (int[])currentOrder.Clone();
        }
        return;
    }

    for (var i = 0; i < checkpoints.Length; i++)
    {
        if (!currentOrder.Contains(i))
        {
            currentOrder[currentIndex] = i;
            if (checkpoints.GetPathLength(currentOrder.Take(currentIndex + 1).ToArray()) >= minLength)
            {
                continue;
            }
            FindBestOrderRecursive(checkpoints, currentOrder, currentIndex + 1, ref bestOrder, ref minLength);
        }
    }
}

