    email.value = emailController.value.text;

    userStorage.setUserName(userName.value);
    userStorage.setPhoneNumber(phoneNumber.value);
    userStorage.setEmail(email.value);
    userStorage.setCountryCode(selectedCountryCode.value);

    updateCanSaveChanges();
  }

  void fetchProfileData() {
    userName.value = userStorage.getUserName();
    phoneNumber.value = userStorage.getPhoneNumber();
    email.value = userStorage.getEmail();

      String storedCountryCode = userStorage.getCountryCode();
    if (storedCountryCode.isNotEmpty) {
      selectedCountryCode.value = storedCountryCode;
      selectedCountryFlag.value = countryFlagsAndCodes.entries
          .firstWhere((entry) => entry.value == storedCountryCode, orElse: () => countryFlagsAndCodes.entries.first)
          .key;
    } else {
      selectedCountryFlag.value = countryFlagsAndCodes.entries.first.key;
      selectedCountryCode.value = countryFlagsAndCodes.entries.first.value;
    }

    userNameController.value.text = userName.value;
    phoneNumberController.value.text = phoneNumber.value;
    emailController.value.text = email.value;
