      if (phoneNumberError != null) {
        Get.snackbar("Error", phoneNumberError);
      }
      if (emailError != null) {
        Get.snackbar("Error", emailError);
      }
    }
    updateCanSaveChanges();
  }

  void logout() {
    userStorage.deleteUserData();
    Get.offAllNamed(AppLinksRoutes.LOGIN);
  }
