actor ChangeTracker<Root: Observable, Value: Sendable>: Sendable {
    private let object: Root
    private let keyPath: KeyPath<Root, Value>
    private(set) var changes: [Value] = []
    private var observation: Any?

    init(object: Root, keyPath: KeyPath<Root, Value>) async {
        self.object = object
        self.keyPath = keyPath
        await setupObservation()
    }

    private func setupObservation() async {
        observation = withObservationTracking({ [weak self] in
            let _ = self?.object[keyPath: await self!.keyPath]
        }, onChange: {
            Task { [weak self] in
                guard let self = self else { return }
                let newValue = await self.getCurrentValue()
                await self.recordChange(newValue)
            }
        })

        // Immediately capture the initial value
        Task {
            await self.recordChange(self.getCurrentValue())
        }
    }

    private func recordChange(_ newValue: Value) {
        changes.append(newValue)
    }

    func getCurrentValue() -> Value {
        object[keyPath: keyPath]
    }

    func resetChanges() {
        changes.removeAll()
    }

    deinit {
        observation = nil // This will cancel the observation
    }
}
