export default function Dashboard() {
  const { user, assessment, loading, error } = useUser();

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900">
        <div className="text-xl text-gray-600 dark:text-gray-300">Loading...</div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-100 dark:bg-gray-900">
        <div className="text-xl text-red-600 dark:text-red-400">{error}</div>
      </div>
    );
  }

  const renderInfoCard = (title: string, content: React.ReactNode) => (
    <div className="bg-white dark:bg-gray-800 p-6 rounded-lg shadow-lg">
      <h2 className="text-xl font-semibold mb-4 text-gray-800 dark:text-white border-b pb-2">
        {title}
      </h2>
      {content}
    </div>
  );

  return (
    <div className="min-h-screen p-8 bg-gray-100 dark:bg-gray-900">
      <div className="max-w-6xl mx-auto">
        <div className="flex items-center justify-between mb-8">
          <h1 className="text-3xl font-bold text-gray-800 dark:text-white">
            Welcome, {user?.username}!
          </h1>
        </div>

        {assessment ? (
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
            {renderInfoCard("Personal Information",
              <div className="space-y-2 text-gray-600 dark:text-gray-300">
                <p><span className="font-semibold">Age:</span> {assessment.personalInfo.age}</p>
                <p><span className="font-semibold">Gender:</span> {assessment.personalInfo.gender}</p>
                <p><span className="font-semibold">Location:</span> {assessment.personalInfo.location}</p>
              </div>
            )}

            {renderInfoCard("Current Fitness",
              <div className="space-y-4 text-gray-600 dark:text-gray-300">
                <div>
