      {showBars && (
        <>
          <div className="absolute top-0 w-full h-16 bg-black transition-all duration-1000 ease-in-out" />
          <div className="absolute bottom-0 w-full h-16 bg-black transition-all duration-1000 ease-in-out" />
        </>
      )}

      <div className="absolute bottom-20 left-0 right-0 mx-auto p-4 bg-black bg-opacity-75 text-white max-w-2xl">
        {currentStep < dialogue.length ? (
          <>
            <div className="font-bold mb-2">{dialogue[currentStep].speaker}:</div>
            <div>{currentText}</div>
          </>
        ) : (
          <div className="text-center text-2xl font-bold">To be continued...</div>
        )}
      </div>

      {!audioStarted && (
        <button
