import React, { useState, useEffect } from 'react';

interface ProfitStats {
  totalProfit: number;
  successfulFrontRuns: number;
  failedAttempts: number;
}

const BaseChainScannerDashboard: React.FC = () => {
  const [profitStats, setProfitStats] = useState<ProfitStats>({
    totalProfit: 0,
    successfulFrontRuns: 0,
    failedAttempts: 0,
  });
  const [isConnected, setIsConnected] = useState<boolean>(false);
  const [currentBlock, setCurrentBlock] = useState<number>(0);
  const [pendingTransactions, setPendingTransactions] = useState<number>(0);

    // Placeholder data for simulation
    useEffect(() => {
        const intervalId = setInterval(() => {
            setProfitStats(prevStats => ({
                totalProfit: prevStats.totalProfit + (Math.random() * 0.01), // Simulate profit increase
                successfulFrontRuns: prevStats.successfulFrontRuns + (Math.random() > 0.8 ? 1 : 0), // Simulate successful runs
                failedAttempts: prevStats.failedAttempts + (Math.random() > 0.9 ? 1 : 0), // Simulate failed attempts
            }));
            setIsConnected(Math.random() > 0.2); // Simulate connection status
            setCurrentBlock(prevBlock => prevBlock + 1);
            setPendingTransactions(Math.floor(Math.random() * 20));
        }, 2000);

        return () => clearInterval(intervalId);
    }, []);

  const successRate =
    profitStats.successfulFrontRuns + profitStats.failedAttempts > 0
      ? (profitStats.successfulFrontRuns / (profitStats.successfulFrontRuns + profitStats.failedAttempts)) * 100
      : 0;

  return (
    <div className="bg-gray-900 text-white min-h-screen p-8">
      <div className="text-center mb-8">
        <h1 className="text-3xl font-bold">Base Chain Front-Runner Dashboard</h1>
        <p className="text-gray-400 mt-2">
          Monitoring Base Chain for profitable opportunities.
        </p>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
        {/* Profit Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Total Profit</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-green-500">
              {profitStats.totalProfit.toFixed(4)} ETH
            </span>
            <span className="text-gray-400 ml-2">💰</span>
          </div>
        </div>

        {/* Success Rate Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Success Rate</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-blue-500">
              {successRate.toFixed(2)}%
            </span>
            <span className="text-gray-400 ml-2">📊</span>
          </div>
        </div>

        {/* Status Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Scanner Status</h2>
          <div className="flex items-center">
            <span
              className={`text-3xl font-bold ${
                isConnected ? 'text-green-500' : 'text-red-500'
              }`}
            >
              {isConnected ? 'Connected' : 'Disconnected'}
            </span>
            <span className="text-gray-400 ml-2">
              {isConnected ? '✅' : '❌'}
            </span>
          </div>
        </div>

        {/* Current Block Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Current Block</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-yellow-500">
              {currentBlock}
            </span>
            <span className="text-gray-400 ml-2">📦</span>
          </div>
        </div>

        {/* Pending Transactions Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Pending Transactions</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-purple-500">
                {pendingTransactions}
            </span>
            <span className="text-gray-400 ml-2">🔍</span>
          </div>
        </div>

        {/* Successful Runs Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Successful Runs</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-green-500">
              {profitStats.successfulFrontRuns}
            </span>
            <span className="text-gray-400 ml-2">✅</span>
          </div>
        </div>

        {/* Failed Attempts Card */}
        <div className="bg-gray-800 rounded-lg p-6 shadow-lg">
          <h2 className="text-xl font-semibold mb-4">Failed Attempts</h2>
          <div className="flex items-center">
            <span className="text-3xl font-bold text-red-500">
              {profitStats.failedAttempts}
            </span>
            <span className="text-gray-400 ml-2">❌</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default BaseChainScannerDashboard;

// Add these imports
import axios from 'axios';
import { ethers } from 'ethers';

// Add BaseScan configuration
const BASESCAN_CONFIG = {
  API_KEY: 'YOUR_BASESCAN_API_KEY', // You'll need to get an API key from BaseScan
  BASE_URL: 'https://api.basescan.org/api',
  WS_URL: 'wss://base-mainnet.g.alchemy.com/v2/YOUR_ALCHEMY_KEY' // For real-time updates
};

interface BaseScanTransaction {
  hash: string;
  from: string;
  to: string;
  value: string;
  gasPrice: string;
  gasUsed: string;
  timeStamp: string;
  methodId: string;
  functionName: string;
}

// Add this to your existing interfaces
interface DetailedTicker extends TickerData {
  // ... existing properties ...
  baseScanData: {
    recentTransactions: BaseScanTransaction[];
    blockNumber: string;
    pendingTxns: number;
  };
}

// Add BaseScan service
const BaseScanService = {
  async getLatestTransactions(): Promise<BaseScanTransaction[]> {
    try {
      const response = await axios.get(`${BASESCAN_CONFIG.BASE_URL}`, {
        params: {
          module: 'account',
          action: 'txlist',
          address: 'latest',
          startblock: 0,
          endblock: 99999999,
          page: 1,
          offset: 100,
          sort: 'desc',
          apikey: BASESCAN_CONFIG.API_KEY
        }
      });
      return response.data.result;
    } catch (error) {
      console.error('Error fetching BaseScan transactions:', error);
      return [];
    }
  },

  // WebSocket connection for real-time updates
  setupWebSocket(): WebSocket {
    const ws = new WebSocket(BASESCAN_CONFIG.WS_URL);

    ws.onopen = () => {
      ws.send(JSON.stringify({
        jsonrpc: '2.0',
        id: 1,
        method: 'eth_subscribe',
        params: ['newHeads']
      }));
    };

    return ws;
  }
};

// Add this to your component
const [baseScanWs, setBaseScanWs] = useState<WebSocket | null>(null);

// Modify useEffect for BaseScan integration
useEffect(() => {
  let ws: WebSocket | null = null;

  const setupBaseScanConnection = async () => {
    // Initial load of transactions
    const transactions = await BaseScanService.getLatestTransactions();
    updateBaseScanData(transactions);

    // Setup WebSocket for real-time updates
    ws = BaseScanService.setupWebSocket();
    setBaseScanWs(ws);

    ws.onmessage = async (event) => {
      const data = JSON.parse(event.data);
      if (data.params?.result?.number) {
        // New block arrived, fetch new transactions
        const newTransactions = await BaseScanService.getLatestTransactions();
        updateBaseScanData(newTransactions);
      }
    };
  };

  if (isSimulating) {
    setupBaseScanConnection();
  }

  return () => {
    if (ws) {
      ws.close();
    }
  };
}, [isSimulating]);

// Add BaseScan transaction display component
const BaseScanTransactionDisplay = () => {
  const transactions = ticker.baseScanData.recentTransactions;

  return (
    <div className="bg-white p-6 rounded-lg shadow">
      <div className="flex justify-between items-center mb-4">
        <h3 className="text-lg font-semibold">Base Network Transactions</h3>
        <span className="text-sm text-gray-500">
          Block: {ticker.baseScanData.blockNumber}
        </span>
      </div>
      <div className="overflow-x-auto">
        <table className="min-w-full">
          <thead>
            <tr className="bg-gray-50">
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Hash</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">From</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">To</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Value</th>
              <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Method</th>
            </tr>
          </thead>
          <tbody className="bg-white divide-y divide-gray-200">
            {transactions.map((tx, index) => (
              <tr key={index}>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-mono">
                  <a 
                    href={`https://basescan.org/tx/${tx.hash}`}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-blue-600 hover:text-blue-800"
                  >
                    {tx.hash.substring(0, 10)}...
                  </a>
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-mono">
                  {tx.from.substring(0, 8)}...
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm font-mono">
                  {tx.to.substring(0, 8)}...
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm">
                  {ethers.utils.formatEther(tx.value)} ETH
                </td>
                <td className="px-6 py-4 whitespace-nowrap text-sm">
                  {tx.functionName || tx.methodId}
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};

// Add MEV Detection component
const MEVDetectionDisplay = () => {
  const transactions = ticker.baseScanData.recentTransactions;
  const mevTransactions = transactions.filter(tx => {
    // Basic MEV detection logic
    const isHighGasPrice = ethers.utils.formatUnits(tx.gasPrice, 'gwei') > 100;
    const isMethodMatch = tx.methodId.startsWith('0x'); // Add specific method IDs
    return isHighGasPrice && isMethodMatch;
  });

  return (
    <div className="bg-gray-900 text-white p-4 rounded-lg">
      <h3 className="text-lg font-semibold mb-3">MEV Activity</h3>
      <div className="space-y-2">
        {mevTransactions.map((tx, index) => (
          <div key={index} className="flex justify-between items-center border-b border-gray-700 pb-2">
            <span className="font-mono text-sm">{tx.hash.substring(0, 10)}...</span>
            <span className="text-yellow-400">
              {ethers.utils.formatUnits(tx.gasPrice, 'gwei')} gwei
            </span>
            <span className="text-green-400">
              {ethers.utils.formatEther(tx.value)} ETH
            </span>
          </div>
        ))}
      </div>
    </div>
  );
};

