      const txns = await fetchTransactions(username);
      setTransactions(txns);
    } catch (err) {
      alert(`Error fetching transactions: ${err.response?.data?.detail || err.message}`);
    }
  };

  const handleGetAdvice = async () => {
    try {
      const resp = await getAdvice(username, question);
      setAdvice(resp.advice);
    } catch (err) {
      alert(`Error getting advice: ${err.response?.data?.detail || err.message}`);
    }
  };

  return (
    <div style={{ border: '1px solid #ccc', padding: '1rem', marginTop: '1rem' }}>
      <h2>Dashboard</h2>
      {!plaidLinked ? (
        <button onClick={handleLinkPlaid}>Link Plaid</button>
      ) : (
        <button onClick={handleFetchTransactions}>Fetch Transactions</button>
      )}
      <div style={{ marginTop: '1rem' }}>
        <h4>Transactions</h4>
        <ul>
          {transactions.map((tx, idx) => (
            <li key={idx}>
              {tx.date} | {tx.name} | ${tx.amount}
            </li>
          ))}
        </ul>
      </div>
      <div style={{ marginTop: '1rem' }}>
        <h4>Ask Advice</h4>
        <input
          type="text"
          placeholder="Should I invest or pay off debt?"
          value={question}
          onChange={(e) => setQuestion(e.target.value)}
          style={{ width: '60%' }}
        />
        <button onClick={handleGetAdvice} style={{ marginLeft: '1rem' }}>Ask</button>
        {advice && <p><strong>Advice:</strong> {advice}</p>}
      </div>
    </div>
  );
}

export default Dashboard;

