import React, { useState } from 'react';
import { TrashIcon, PlusIcon } from '@heroicons/react/24/solid';

interface Transaction {
  id: number;
  type: 'income' | 'expense';
  amount: number;
  description: string;
  date: Date;
}

const BudgetingApp: React.FC = () => {
  const [balance, setBalance] = useState<number>(0);
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [newTransaction, setNewTransaction] = useState<{ type: 'income' | 'expense'; amount: string; description: string }>({ type: 'income', amount: '', description: '' });
  const [error, setError] = useState<string>('');
  const [descriptionFilter, setDescriptionFilter] = useState('');
  const [startDate, setStartDate] = useState('');
  const [endDate, setEndDate] = useState('');

  const addTransaction = () => {
    const amount = parseFloat(newTransaction.amount);
    if (!newTransaction.description.trim()) {
      setError('Description is required');
      return;
    }
    if (isNaN(amount) || amount <= 0) {
      setError('Please enter a valid amount');
      return;
    }

    setError('');

    const transaction: Transaction = {
      id: Date.now(),
      type: newTransaction.type,
      amount,
      description: newTransaction.description,
      date: new Date(),
    };

    setTransactions([...transactions, transaction]);
    setBalance(balance + (transaction.type === 'income' ? amount : -amount));
    setNewTransaction({ type: 'income', amount: '', description: '' });
  };

  const deleteTransaction = (id: number) => {
    const transactionToRemove = transactions.find(t => t.id === id);
    if (transactionToRemove) {
      setTransactions(transactions.filter(t => t.id !== id));
      setBalance(balance - (transactionToRemove.type === 'income' ? transactionToRemove.amount : -transactionToRemove.amount));
    }
  };

  const filteredTransactions = transactions.filter(t => {
    if (descriptionFilter && !t.description.toLowerCase().includes(descriptionFilter.toLowerCase())) {
      return false;
    }
    const transactionDate = new Date(t.date).setHours(0, 0, 0, 0);
    if (startDate) {
      const start = new Date(startDate).setHours(0, 0, 0, 0);
      if (transactionDate < start) {
        return false;
      }
    }
    if (endDate) {
      const end = new Date(endDate).setHours(0, 0, 0, 0);
      if (transactionDate > end) {
        return false;
      }
    }
    return true;
  });

  const totalIncome = transactions.filter(t => t.type === 'income').reduce((sum, t) => sum + t.amount, 0);
  const totalExpenses = transactions.filter(t => t.type === 'expense').reduce((sum, t) => sum + t.amount, 0);

  return (
    <div className="min-h-screen bg-gray-100 py-6 flex flex-col justify-center sm:py-12">
      <div className="relative py-3 sm:max-w-xl sm:mx-auto">
        <div className="absolute inset-0 bg-gradient-to-r from-blue-600 to-blue-400 shadow-lg transform -skew-y-6 sm:skew-y-0 sm:-rotate-6 sm:rounded-3xl"></div>
        <div className="relative px-4 py-10 bg-white shadow-lg sm:rounded-3xl sm:p-20">
          <h1 className="text-2xl font-bold text-center text-gray-800 mb-6">Budgeting App</h1>
          <div className="bg-blue-100 p-6 rounded-lg mb-6">
            <p className="text-lg text-gray-700">Current Balance</p>
            <p className="text-3xl font-bold text-blue-600">${balance.toFixed(2)}</p>
            <div className="mt-4 flex justify-between">
              <div>
                <p className="text-sm text-green-600">Income</p>
                <p className="text-lg font-semibold text-green-600">${totalIncome.toFixed(2)}</p>
              </div>
              <div>
                <p className="text-sm text-red-600">Expenses</p>
                <p className="text-lg font-semibold text-red-600">${totalExpenses.toFixed(2)}</p>
              </div>
            </div>
          </div>
          <div className="mb-6">
            <h2 className="text-lg font-semibold text-gray-700 mb-4">Add New Transaction</h2>
            {error && <div className="bg-red-100 border border-red-400 text-red-700 px-4 py-3 rounded relative mb-4" role="alert">{error}</div>}
            <div className="space-y-4">
              <select value={newTransaction.type} onChange={(e) => setNewTransaction({ ...newTransaction, type: e.target.value as 'income' | 'expense' })} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500">
                <option value="income">Income</option>
                <option value="expense">Expense</option>
              </select>
              <input type="number" placeholder="Amount" value={newTransaction.amount} onChange={(e) => setNewTransaction({ ...newTransaction, amount: e.target.value })} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500" />
              <input type="text" placeholder="Description" value={newTransaction.description} onChange={(e) => setNewTransaction({ ...newTransaction, description: e.target.value })} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500" />
              <button onClick={addTransaction} className="w-full bg-blue-600 text-white p-3 rounded-lg hover:bg-blue-700 transition flex justify-center items-center">
                <PlusIcon className="h-5 w-5 mr-2" /> Add Transaction
              </button>
            </div>
          </div>
          <div className="mb-6">
            <h2 className="text-lg font-semibold text-gray-700 mb-4">Filter Transactions</h2>
            <div className="space-y-4">
              <input type="text" placeholder="Search by description" value={descriptionFilter} onChange={(e) => setDescriptionFilter(e.target.value)} className="w-full p-3 border rounded-lg focus:ring-2 focus:ring-blue-500" />
              <div className="flex space-x-4">
                <input type="date" value={startDate} onChange={(e) => setStartDate(e.target.value)} className="w-1/2 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500" />
                <input type="date" value={endDate} onChange={(e) => setEndDate(e.target.value)} className="w-1/2 p-3 border rounded-lg focus:ring-2 focus:ring-blue-500" />
              </div>
            </div>
          </div>
          <div>
            <h2 className="text-lg font-semibold text-gray-700 mb-4">Transactions</h2>
            {filteredTransactions.length === 0 ? (
              <p className="text-gray-500">No transactions found.</p>
            ) : (
              <ul className="space-y-2">
                {filteredTransactions.map((transaction) => (
                  <li key={transaction.id} className="flex justify-between items-center p-4 border rounded-lg">
                    <div>
                      <p className="font-medium">{transaction.description}</p>
                      <p className="text-sm text-gray-500">{new Date(transaction.date).toLocaleDateString()}</p>
                    </div>
                    <p className={`font-semibold ${transaction.type === 'income' ? 'text-green-600' : 'text-red-600'}`}>{transaction.type === 'income' ? '+' : '-'}${transaction.amount.toFixed(2)}</p>
                    <button onClick={() => deleteTransaction(transaction.id)} className="text-red-500 hover:text-red-700">
                      <TrashIcon className="h-5 w-5" />
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default BudgetingApp;

