import { useState, useEffect } from 'react';
import { LineChart, Line, XAxis, YAxis, CartesianGrid, Tooltip, Legend, ResponsiveContainer } from 'recharts';

const MEVSimulator = () => {
  const [metrics, setMetrics] = useState({
    usdcConverted: 0,
    profitFromAttacks: 0,
    lossFromAttacks: 0,
    totalUSDConverted: 0,
    totalUSDStabled: 0,
    successRate: 0,
    ethPrice: 3000
  });

  const generateMockTransaction = () => ({
    gasPrice: `0x${Math.floor(Math.random() * 1000000).toString(16)}`,
    gasUsed: 21000,
    hash: `0x${Math.random().toString(16).substr(2, 64)}`,
    to: `0x${Math.random().toString(16).substr(2, 40)}`
  });

  useEffect(() => {
    const interval = setInterval(() => {
      const tx = generateMockTransaction();
      const success = Math.random() > 0.3;
      const profit = success ? Math.random() * 2 : 0;
      const usdcValue = profit * metrics.ethPrice;

      setMetrics(prev => ({
        ...prev,
        ethPrice: 3000 + (Math.random() * 200 - 100),
        usdcConverted: prev.usdcConverted + usdcValue,
        profitFromAttacks: prev.profitFromAttacks + (success ? usdcValue : 0),
        lossFromAttacks: prev.lossFromAttacks + (!success ? 
          parseInt(tx.gasPrice, 16) * tx.gasUsed * prev.ethPrice : 0),
        totalUSDConverted: prev.totalUSDConverted + usdcValue,
        totalUSDStabled: prev.totalUSDStabled + (usdcValue * 0.85),
        successRate: ((prev.profitFromAttacks + (success ? 1 : 0)) / 
          (prev.profitFromAttacks + prev.lossFromAttacks + 1)) * 100
      }));
    }, 1500);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="simulator-container">
      <div className="dashboard-panel">
        <div className="price-header">
          <h2>ETH/USDC: ${metrics.ethPrice.toFixed(2)}</h2>
        </div>

        <div className="financial-metrics">
          <div className="metric-box success">
            <h3>Profit</h3>
            <p>${metrics.profitFromAttacks.toFixed(2)}</p>
          </div>
          <div className="metric-box danger">
            <h3>Losses</h3>
            <p>${metrics.lossFromAttacks.toFixed(2)}</p>
          </div>
          <div className="metric-box info">
            <h3>Success Rate</h3>
            <p>{metrics.successRate.toFixed(1)}%</p>
          </div>
        </div>

        <div className="conversion-chart">
          <ResponsiveContainer width="100%" height={300}>
            <LineChart data={[
              {name: 'USDC Converted', value: metrics.usdcConverted},
              {name: 'Stabilized', value: metrics.totalUSDStabled}
            ]}>
              <CartesianGrid strokeDasharray="3 3" />
              <XAxis dataKey="name" />
              <YAxis />
              <Tooltip />
              <Legend />
              <Line type="monotone" dataKey="value" stroke="#82ca9d" />
            </LineChart>
          </ResponsiveContainer>
        </div>
      </div>

      <style jsx>{`
        .simulator-container {
          padding: 2rem;
          background: #1a1a1a;
          color: white;
          border-radius: 12px;
          font-family: 'Courier New', monospace;
        }
        .dashboard-panel {
          border: 1px solid #333;
          border-radius: 8px;
          padding: 1.5rem;
        }
        .financial-metrics {
          display: grid;
          grid-template-columns: repeat(3, 1fr);
          gap: 1rem;
