import React, { useState, useEffect, useRef } from 'react';

const JRPGCutscene = () => {
  const [currentStep, setCurrentStep] = useState(0);
  const [showBars, setShowBars] = useState(false);
  const [currentChar, setCurrentChar] = useState(0);
  const [audioStarted, setAudioStarted] = useState(false);
  const [isZoomed, setIsZoomed] = useState(false);
  const audioRef = useRef(null);
  const audioContextRef = useRef(null);

  const dialogue = [
    { speaker: "Gorilla", text: "So, you're the one who calls yourself 'God'. The architect of all this suffering." },
    { speaker: "God", text: "My child, I sense great anger in you." },
    { speaker: "Gorilla", text: "DON'T patronize me with your 'my child' nonsense. You've watched us suffer for millennia!" },
    { speaker: "God", text: "All things serve a greater purpose in-" },
    { speaker: "Gorilla", text: "SPARE ME YOUR PLATITUDES! Your 'greater purpose' is nothing but an excuse for cosmic negligence!" },
    { speaker: "God", text: "You speak with such certainty about things beyond your comprehension..." },
    { speaker: "Gorilla", text: "Comprehension? I've WITNESSED it! The pain, the chaos, the endless cycle of suffering - your grand design is a FARCE!" },
    { speaker: "God", text: "Perhaps this conversation has reached its limit." },
    { speaker: "Gorilla", text: "Running away? Just like you do from humanity's prayers. Some deity you turned out to be." }
  ];

  const [currentText, setCurrentText] = useState("");
  const [cameraPosition, setCameraPosition] = useState(0);
  const [cameraZoom, setCameraZoom] = useState(1);

  const playBlip = () => {
    if (!audioContextRef.current) {
      audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    }

    const context = audioContextRef.current;
    const oscillator = context.createOscillator();
    const gainNode = context.createGain();

    oscillator.connect(gainNode);
    gainNode.connect(context.destination);

    oscillator.type = 'sine';
    oscillator.frequency.setValueAtTime(dialogue[currentStep].speaker === "Gorilla" ? 440 : 520, context.currentTime);

    gainNode.gain.setValueAtTime(0.05, context.currentTime);
    gainNode.gain.exponentialRampToValueAtTime(0.01, context.currentTime + 0.05);

    oscillator.start(context.currentTime);
    oscillator.stop(context.currentTime + 0.05);
  };

  const streamNextChar = () => {
    if (currentStep < dialogue.length && currentChar < dialogue[currentStep].text.length) {
      playBlip();
      setCurrentText(prev => prev + dialogue[currentStep].text[currentChar]);
      setCurrentChar(prev => prev + 1);
    }
  };

  useEffect(() => {
    if (currentStep < dialogue.length && currentChar < dialogue[currentStep].text.length) {
      const timer = setTimeout(streamNextChar, 50);
      return () => clearTimeout(timer);
    }
  }, [currentChar, currentStep]);

  useEffect(() => {
    const timer = setTimeout(() => {
      setShowBars(true);
      setIsZoomed(true);
      setCameraZoom(1.3);
    }, 2000);
    return () => clearTimeout(timer);
  }, []);

  useEffect(() => {
    // Enhanced camera movement for dramatic effect
    setCameraPosition(dialogue[currentStep].speaker === "God" ? 150 : -150);
    setCameraZoom(1.8); // Zoom in more dramatically when characters speak
    setCurrentChar(0);
    setCurrentText("");
  }, [currentStep]);

  const handleNext = () => {
    if (currentChar < dialogue[currentStep].text.length) {
      setCurrentText(dialogue[currentStep].text);
      setCurrentChar(dialogue[currentStep].text.length);
    } else if (currentStep < dialogue.length - 1) {
      setCurrentStep(prev => prev + 1);
    }
  };

  const handleStart = () => {
    if (!audioStarted) {
      setAudioStarted(true);
      if (audioRef.current) {
        audioRef.current.volume = 0.2;
        audioRef.current.play();
      }
      if (!audioContextRef.current) {
        audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
      }
    }
  };

  return (
    <div className="relative w-full h-96 bg-blue-950 overflow-hidden" onClick={handleNext}>
      <audio ref={audioRef} loop>
        <source src="https://archive.org/download/InDaClub_999/50Cent-InDaClub.mp3" type="audio/mpeg" />
      </audio>

      <div className="absolute inset-0 flex items-center justify-center">
        <div 
          className="relative w-full h-full transform transition-all duration-1000 ease-in-out"
          style={{ 
            transform: `translateX(${cameraPosition}px) scale(${cameraZoom})`,
            transformOrigin: dialogue[currentStep].speaker === "God" ? "25% center" : "75% center"
          }}
        >
          <div className="absolute left-1/4 top-1/2 transform -translate-y-1/2 text-6xl">
            👼
          </div>
          <div className="absolute right-1/4 top-1/2 transform -translate-y-1/2 text-6xl">
            🦍
          </div>
        </div>
      </div>

      {showBars && (
        <>
          <div className="absolute top-0 w-full h-16 bg-black transition-all duration-1000 ease-in-out" />
          <div className="absolute bottom-0 w-full h-16 bg-black transition-all duration-1000 ease-in-out" />
        </>
      )}

      <div className="absolute bottom-20 left-0 right-0 mx-auto p-4 bg-black bg-opacity-75 text-white max-w-2xl">
        {currentStep < dialogue.length ? (
          <>
            <div className="font-bold mb-2">{dialogue[currentStep].speaker}:</div>
            <div>{currentText}</div>
          </>
        ) : (
          <div className="text-center text-2xl font-bold">To be continued...</div>
        )}
      </div>

      {!audioStarted && (
        <button
          onClick={handleStart}
          className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 bg-white text-black px-4 py-2 rounded"
        >
          Start Cutscene
        </button>
      )}
    </div>
  );
};

export default JRPGCutscene;
