import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';

const JarvisUI = () => {
  const [isActive, setIsActive] = useState(false);
  const [message, setMessage] = useState('');
  const [particles, setParticles] = useState<number[]>([]);
  const [audioWaves, setAudioWaves] = useState<number[]>([]);

  useEffect(() => {
    setIsActive(true);
    const text = "INITIALIZING JARVIS INTERFACE...";
    let current = '';
    let index = 0;

    const interval = setInterval(() => {
      if (index < text.length) {
        current += text[index];
        setMessage(current);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100);

    // Generate random particles
    setParticles(Array.from({ length: 20 }, (_, i) => i));

    // Generate audio waves
    setAudioWaves(Array.from({ length: 20 }, (_, i) => i));

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-950 flex items-center justify-center p-4">
      <div className="relative w-full max-w-4xl">
        {/* Background Grid */}
        <div className="absolute inset-0 grid grid-cols-12 gap-2 opacity-20">
          {Array.from({ length: 144 }).map((_, i) => (
            <div key={i} className="bg-gray-800 rounded-sm aspect-square" />
          ))}
        </div>

        {/* Main Interface */}
        <motion.div
          initial={{ scale: 0 }}
          animate={{ scale: 1 }}
          transition={{ duration: 1 }}
          className="relative z-10"
        >
          {/* Central Core */}
          <motion.div
            animate={{
              scale: [1, 1.1, 1],
            }}
            transition={{
              duration: 2,
              repeat: Infinity,
              ease: "easeInOut"
            }}
            className="w-32 h-32 rounded-full bg-gray-800 mx-auto relative"
          >
            <div className="absolute inset-2 rounded-full bg-gray-700 animate-pulse" />
            <div className="absolute inset-4 rounded-full bg-gray-600 animate-pulse" />
          </motion.div>

          {/* Rotating Rings */}
          {[1, 2, 3].map((ring) => (
            <motion.div
              key={ring}
              animate={{ rotate: 360 }}
              transition={{
                duration: 8 * ring,
                repeat: Infinity,
                ease: "linear"
              }}
              className={`absolute top-1/2 left-1/2 -translate-x-1/2 -translate-y-1/2 border-2 border-gray-600/30 rounded-full`}
              style={{
                width: `${ring * 200}px`,
                height: `${ring * 200}px`,
              }}
            />
          ))}

          {/* Floating Particles */}
          <AnimatePresence>
            {particles.map((i) => (
              <motion.div
                key={i}
                initial={{ 
                  opacity: 0,
                  x: Math.random() * 400 - 200,
                  y: Math.random() * 400 - 200
                }}
                animate={{
                  opacity: [0, 1, 0],
                  x: Math.random() * 400 - 200,
                  y: Math.random() * 400 - 200
                }}
                exit={{ opacity: 0 }}
                transition={{
                  duration: 3,
                  repeat: Infinity,
                  repeatType: "reverse"
                }}
                className="absolute top-1/2 left-1/2 w-2 h-2 bg-gray-600 rounded-full"
              />
            ))}
          </AnimatePresence>

          {/* Audio Waves */}
          <div className="absolute bottom-10 left-1/2 -translate-x-1/2 flex gap-1">
            {audioWaves.map((i) => (
              <motion.div
                key={i}
                animate={{
                  scaleY: [0.2, 1, 0.2],
                  opacity: [0.5, 1, 0.5]
                }}
                transition={{
                  duration: 1,
                  repeat: Infinity,
                  ease: "easeInOut",
                  delay: i * 0.1
                }}
                className="w-1 h-10 bg-gray-600 rounded-full"
              />
            ))}
          </div>

          {/* Status Text */}
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="text-center mt-8"
          >
            <h2 className="text-gray-400 text-2xl font-mono mb-4">{message}</h2>

            {/* Status Indicators */}
            <div className="flex justify-center gap-4 mt-4">
              {['SYSTEMS', 'NETWORK', 'SECURITY'].map((system) => (
                <div key={system} className="text-center">
                  <div className="w-4 h-4 bg-gray-500 rounded-full mx-auto animate-pulse" />
                  <p className="text-gray-300 text-sm mt-2">{system}</p>
                </div>
              ))}
            </div>
          </motion.div>

          {/* Interactive Buttons */}
          <div className="flex justify-center gap-4 mt-8">
            {['SCAN', 'ANALYZE', 'DEPLOY'].map((action) => (
              <motion.button
                key={action}
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.95 }}
                className="px-6 py-2 bg-gray-600 text-gray-100 rounded-lg font-mono
                         hover:bg-gray-500 transition-colors duration-200"
              >
                {action}
              </motion.button>
            ))}
          </div>
        </motion.div>
      </div>
    </div>
  );
};

