import { useState, useEffect } from 'react';
import { motion, useMotionValue, useTransform, animate, useAnimation, AnimatePresence } from 'framer-motion';

const BusinessCard = () => {
  const controls = useAnimation();
  const x = useMotionValue(0);
  const y = useMotionValue(0);

  // Enhanced rotation transforms with more subtle movement
  const rotateX = useTransform(y, [-100, 100], [15, -15]);
  const rotateY = useTransform(x, [-100, 100], [-15, 15]);

  // Perspective and scale transforms for depth
  const perspective = useTransform(y, [-100, 100], [1000, 1000]);
  const scale = useMotionValue(1);

  // Floating animation
  useEffect(() => {
    const floatAnimation = async () => {
      while (true) {
        await controls.start({
          y: [0, -10, 0],
          transition: {
            duration: 3,
            ease: "easeInOut",
            repeat: Infinity,
          },
        });
      }
    };
    floatAnimation();
  }, [controls]);

  const handleMouseMove = (event: React.MouseEvent<HTMLDivElement>) => {
    const rect = event.currentTarget.getBoundingClientRect();
    const centerX = rect.left + rect.width / 2;
    const centerY = rect.top + rect.height / 2;

    const mouseX = event.clientX - centerX;
    const mouseY = event.clientY - centerY;

    // Smoother animation with spring physics
    animate(x, mouseX, {
      type: "spring",
      stiffness: 150,
      damping: 15,
      mass: 0.1
    });

    animate(y, mouseY, {
      type: "spring",
      stiffness: 150,
      damping: 15,
      mass: 0.1
    });

    // Scale up slightly on hover
    animate(scale, 1.05, {
      type: "spring",
      stiffness: 200,
      damping: 20
    });
  };

  const handleMouseLeave = () => {
    // Smooth return to original position
    animate(x, 0, {
      type: "spring",
      stiffness: 200,
      damping: 25
    });
    animate(y, 0, {
      type: "spring",
      stiffness: 200,
      damping: 25
    });
    animate(scale, 1, {
      type: "spring",
      stiffness: 200,
      damping: 25
    });
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 flex items-center justify-center p-4">
      <motion.div
        animate={controls}
        className="relative perspective-1000"
        onMouseMove={handleMouseMove}
        onMouseLeave={handleMouseLeave}
        style={{
          rotateX,
          rotateY,
          scale,
          transformStyle: "preserve-3d",
        }}
      >
        <AnimatePresence mode="wait">
          <motion.div
            className="w-96 h-56 rounded-xl cursor-pointer transform-gpu"
          >
            {/* Front of the card */}
            <motion.div
              className={`absolute w-full h-full backface-hidden opacity-100`}
            >
              <div className="w-full h-full bg-gradient-to-br from-blue-600 via-blue-500 to-purple-600 rounded-xl p-8 text-white shadow-lg">
                <div className="flex items-start justify-between">
                  <div>
                    <motion.h1 
                      className="text-2xl font-bold mb-1"
                      initial={{ opacity: 0, y: 20 }}
                      animate={{ opacity: 1, y: 0 }}
                      transition={{ delay: 0.2 }}
                    >
                      John Doe
                    </motion.h1>
                    <motion.p 
                      className="text-sm text-blue-100"
                      initial={{ opacity: 0 }}
                      animate={{ opacity: 1 }}
                      transition={{ delay: 0.3 }}
                    >
                      Senior Developer
                    </motion.p>
                  </div>
                  <motion.div 
                    className="bg-white/20 p-2 rounded-lg backdrop-blur-sm"
                    whileHover={{ scale: 1.05 }}
                    whileTap={{ scale: 0.95 }}
                  >
                    <div className="w-12 h-12 bg-white/90 rounded-lg shadow-inner" />
                  </motion.div>
                </div>
                <motion.div 
                  className="mt-8 space-y-2"
                  initial={{ opacity: 0 }}
                  animate={{ opacity: 1 }}
                  transition={{ delay: 0.4 }}
                >
                  <p className="text-sm flex items-center space-x-2">
                    <span className="text-blue-200">Email: </span>
                    <span className="font-light">john.doe@example.com</span>
                  </p>
                  <p className="text-sm flex items-center space-x-2">
                    <span className="text-blue-200">Phone: </span>
                    <span className="font-light">+1 (555) 123-4567</span>
                  </p>
                  <p className="text-sm flex items-center space-x-2">
                    <span className="text-blue-200">Website: </span>
                    <span className="font-light">www.johndoe.dev</span>
                  </p>
                </motion.div>
              </div>
            </motion.div>
          </motion.div>
        </AnimatePresence>
      </motion.div>

      <style jsx global>{`
        .backface-hidden {
          backface-visibility: hidden;
          -webkit-backface-visibility: hidden;
        }
        .perspective-1000 {
          perspective: 1000px;
        }
      `}</style>
    </div>
  );
};

