const JarvisUI = () => {
  const [isActive, setIsActive] = useState(false);
  const [message, setMessage] = useState('');
  const [particles, setParticles] = useState<number[]>([]);
  const [audioWaves, setAudioWaves] = useState<number[]>([]);

  useEffect(() => {
    setIsActive(true);
    const text = "INITIALIZING JARVIS INTERFACE...";
    let current = '';
    let index = 0;

    const interval = setInterval(() => {
      if (index < text.length) {
        current += text[index];
        setMessage(current);
        index++;
      } else {
        clearInterval(interval);
      }
    }, 100);

    // Generate random particles
    setParticles(Array.from({ length: 20 }, (_, i) => i));

    // Generate audio waves
    setAudioWaves(Array.from({ length: 20 }, (_, i) => i));

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="min-h-screen bg-gray-950 flex items-center justify-center p-4">
      <div className="relative w-full max-w-4xl">
        {/* Background Grid */}
        <div className="absolute inset-0 grid grid-cols-12 gap-2 opacity-20">
