i have some code for you to assess and expand upon. in the code cherry blossoms fall in the distance. without changing this layer, i want you to make a foreground where fewer larger cherry blossoms fall. take the perspective into account, base the fall speep on how largew they are so the sceen looks more realistic. here is the code: import { useState, useEffect, useRef } from 'react';

const CherryBlossom = () => {
  const [petals, setPetals] = useState<Array<{ id: number; x: number; y: number; rotation: number; speed: number; startDelay: number; size: number }>>([]);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const petalCount = 30; // Reduced petal count
  const colors = ['#F4C2C2', '#F9B4B4', '#FFB6C1', '#FFD1DC', '#FFE4E1'];

  useEffect(() => {
    const createPetals = () => {
      const newPetals = Array.from({ length: petalCount }, (_, i) => ({
        id: i,
        x: Math.random() * window.innerWidth * 0.4 + window.innerWidth * 0.1, // Start from top-left
        y: -20,
        rotation: Math.random() * 360,
        speed: 0.3 + Math.random() * 0.4,
        startDelay: Math.random() * 3000,
        size: 15 + Math.random() * 10, // Varied size
      }));
      setPetals(newPetals);
    };

    createPetals();
  }, []);

  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;

    const ctx = canvas.getContext('2d');
    if (!ctx) return;

    let animationFrameId: number;

    const animate = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      petals.forEach((petal, index) => {
        if (petal.startDelay > 0) {
          petal.startDelay -= 16;
          return;
        }

        petal.y += petal.speed;
        petal.x += petal.speed * 0.1; // Slight drift to the right
        petal.rotation += 0.2;

        if (petal.y > canvas.height) {
          petal.y = -20;
          petal.x = Math.random() * window.innerWidth * 0.4 + window.innerWidth * 0.1; // Reset X
        }

        const color = colors[index % colors.length];
        ctx.fillStyle = color;
        ctx.font = `${petal.size}px serif`;
        ctx.save();
        ctx.translate(petal.x + petal.size / 2, petal.y + petal.size / 2);
        ctx.rotate((petal.rotation * Math.PI) / 180);
        ctx.fillText('🌸', -petal.size / 2, -petal.size / 2);
        ctx.restore();
      });

      animationFrameId = requestAnimationFrame(animate);
    };

    animate();

    return () => {
      cancelAnimationFrame(animationFrameId);
    };
  }, [petals, colors]);

  return (
    <div className="min-h-screen bg-gradient-to-b from-blue-100 to-white overflow-hidden">
      <canvas ref={canvasRef} className="absolute top-0 left-0 w-full h-full" />
    </div>
  );
};

export default CherryBlossom;

rubiks cube 3d three js import React, { useEffect, useRef } from 'react';
import * as THREE from 'three';

const RubiksCube: React.FC = () => {
  const mountRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (!mountRef.current) return;

    const scene = new THREE.Scene();
    const camera = new THREE.PerspectiveCamera(75, window.innerWidth / window.innerHeight, 0.1, 1000);
    const renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(window.innerWidth, window.innerHeight);
    mountRef.current.appendChild(renderer.domElement);

    const geometry = new THREE.BoxGeometry();
    const materials = [
      new THREE.MeshBasicMaterial({ color: 0xff0000 }),
      new THREE.MeshBasicMaterial({ color: 0x00ff00 }),
      new THREE.MeshBasicMaterial({ color: 0x0000ff }),
      new THREE.MeshBasicMaterial({ color: 0xffff00 }),
      new THREE.MeshBasicMaterial({ color: 0xff00ff }),
      new THREE.MeshBasicMaterial({ color: 0x00ffff }),
    ];
    const cube = new THREE.Mesh(geometry, materials);
    scene.add(cube);

    camera.position.z = 5;

    const animate = function () {
      requestAnimationFrame(animate);
      cube.rotation.x += 0.01;
      cube.rotation.y += 0.01;
      renderer.render(scene, camera);
    };

    animate();

    return () => {
      mountRef.current?.removeChild(renderer.domElement);
    };
  }, []);

  return <div ref={mountRef} className="flex justify-center items-center h-screen bg-gray-800"></div>;
};

export default RubiksCube;

import React, { useState, useRef, useEffect } from 'react';
import { Canvas, useFrame, useThree } from '@react-three/fiber';
import * as THREE from 'three';

const Box = () => {
  const meshRef = useRef<THREE.Mesh>(null);
  const [hovered, setHover] = useState(false);
  const [active, setActive] = useState(false);

  useFrame((state, delta) => {
    if (meshRef.current && active) {
      meshRef.current.rotation.x += delta;
      meshRef.current.rotation.y += delta;
    }
  });

  return (
    <mesh
      ref={meshRef}
      scale={active ? 1.5 : 1}
      onClick={() => setActive(!active)}
      onPointerOver={() => setHover(true)}
      onPointerOut={() => setHover(false)}
    >
      <boxGeometry args={[1, 1, 1]} />
      <meshStandardMaterial color={hovered ? 'hotpink' : 'orange'} />
    </mesh>
  );
};

const Controls = () => {
  const { camera, gl } = useThree();
  useEffect(() => {
    const controls = new (require("three/examples/jsm/controls/OrbitControls").OrbitControls)(camera, gl.domElement);
    controls.enableDamping = true;
    controls.dampingFactor = 0.1;
    return () => {
      controls.dispose();
