  const velocityRef = useRef(0);
  const spinnerRef = useRef<HTMLDivElement>(null);
  const isHoldingRef = useRef(false);
  const lastUpdateRef = useRef(performance.now());

  const animate = () => {
    const now = performance.now();
    const deltaTime = now - lastUpdateRef.current;

    if (deltaTime > 0) {
      rotationRef.current += velocityRef.current * (deltaTime / 1000);

      // Update visual rotation
      if (spinnerRef.current) {
        spinnerRef.current.style.transform = `rotate(${rotationRef.current % 360}deg)`;
      }

      // Apply friction
      const friction = isTurbo ? 0.92 : 0.95;
      velocityRef.current *= friction;

      // Update display values
      setTotalSpins(Math.abs(Math.floor(rotationRef.current / 360)));
      setDisplayVelocity(Math.round(velocityRef.current));
    }

    lastUpdateRef.current = now;

    if (Math.abs(velocityRef.current) > 0.5) {
      requestRef.current = requestAnimationFrame(animate);
    }
