            disabled={isLoading}
            className="w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition duration-300 flex items-center justify-center space-x-2 disabled:opacity-50"
          >
            {isLoading ? (
              <motion.div
                animate={{ rotate: 360 }}
                transition={{
                  repeat: Infinity,
                  duration: 1,
                  ease: "linear"
                }}
              >
                <FaSignInAlt className="animate-spin" />
              </motion.div>
            ) : (
              <>
                <FaSignInAlt />
                <span>Sign In</span>
              </>
            )}
